Based on the provided description, here is a sample Solidity smart contract that meets the requirements:

```solidity
pragma solidity ^0.6.12;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.3.0/contracts/token/ERC20/ERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.3.0/contracts/math/SafeMath.sol";

contract MyToken is ERC20 {
    using SafeMath for uint256;

    address public owner;
    string public symbol;
    string public name;
    uint8 public decimals;
    uint256 private totalSupply;
    bool public crowdsaleenabled;
    uint256 public ethpertoken;
    uint256 public bonusmineth;
    uint256 public bonuspct;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;

    constructor() public {
        owner = msg.sender;
        symbol = "0";
        name = "0";
        decimals = 18;
        totalSupply = 0x1743b34e18439b502000000;
        balanceof[owner] = totalSupply;
        crowdsaleenabled = false;
        ethpertoken = 20000;
        bonusmineth = 0;
        bonuspct = 0;
    }

    function disableCrowdsale() public {
        require(msg.sender == owner);
        crowdsaleenabled = false;
    }

    function setBonus(uint256 _bonusmineth, uint256 _bonuspct) public {
        require(msg.sender == owner);
        bonusmineth = _bonusmineth;
        bonuspct = _bonuspct;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function() external payable {
        require(crowdsaleenabled);
        uint256 amount = msg.value.mul(ethpertoken);
        require(balanceof[owner] >= amount);
        balanceof[owner] -= amount;
        balanceof[msg.sender] += amount;
    }

    function enableCrowdsale() public {
        require(msg.sender == owner);
        crowdsaleenabled = true;
    }

    function withdraw(uint256 amount) public {
        require(msg.sender == owner && address(this).balance >= amount);
        msg.sender.transfer(amount);
    }

    function setTokenPrice(uint256 _ethpertoken) public {
        require(msg.sender == owner);
        ethpertoken = _ethpertoken;
    }

    function acceptOwnership() public {
        require(msg.sender == owner);
        owner = msg.sender;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(balanceof[_from] >= _value && allowance[_from][msg.sender] >= _value);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalSupply -= _value;
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        (bool success, bytes memory data) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,address,bytes)", msg.sender, _value, this, _extraData));
        require(success);
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }

    function transferAnyERC20Token(address _tokenAddress, uint256 _value) public returns (bool success) {
        require(msg.sender == owner);
        (bool success, ) = _tokenAddress.call(abi.encodeWithSignature("transfer(address,uint256)", owner, _value));
        require(success);
        return true;
    }
}
```

This contract includes the described functionality. The contract owner can enable and disable the crowdsale, set the bonus parameters, approve token transfers, withdraw Ether from the contract, set the token price, accept ownership, transfer tokens from one address to another, burn tokens, approve and call other contracts, transfer tokens, and transfer the ownership of the contract. A payable fallback function is also included to handle incoming Ether during the crowdsale. ERC20 standard functions are also implemented for token transfers and approvals. The contract uses SafeMath library from OpenZeppelin to avoid integer overflow and underflow issues.
