pragma solidity >=0.7.0 <0.9.0;

contract CrowdfundingContract {
    uint256 private _fundingGoal;
    uint256 private _amountRaised;
    uint256 private _deadline;
    uint256 private _price;
    mapping(address => uint256) private _balanceOf;
    address private _beneficiary;
    bool private _safeWithdrawal;
    address private _tokenReward; 

    event FundTransfer(address indexed _backer, uint256 _amount, bool _isContribution);
    event GoalReached(address indexed _beneficiary, uint256 _amountRaised);

    function checkGoalReached() public {
        require(block.timestamp >= _deadline, "Deadline not reached");

        if (_amountRaised >= _fundingGoal) {
            _safeWithdrawal = true;
            emit GoalReached(_beneficiary, _amountRaised);
        }
    }

    function deadline() public view returns(uint256) {
        return _deadline;
    }

    function beneficiary() public view returns(address) {
        return _beneficiary;
    }

    function tokenReward() public view returns(address) {
        return _tokenReward;
    }

    function balanceOf(address _addr) public view returns(uint256) {
        return _balanceOf[_addr];
    }

    function fundingGoal() public view returns(uint256) {
        return _fundingGoal;
    }

    function amountRaised() public view returns(uint256) {
        return _amountRaised;
    }

    function price() public view returns(uint256) {
        return _price;
    }

    function safeWithdrawal() public {
        require(block.timestamp >= _deadline, "Deadline not reached");

        if (!_safeWithdrawal) {
            uint256 amount = _balanceOf[msg.sender];
            _balanceOf[msg.sender] = 0;
            (bool success, ) = msg.sender.call{value: amount}("");
            require(success, "Transfer failed");
            emit FundTransfer(msg.sender, amount, false);
        }

        if (_safeWithdrawal && _beneficiary == msg.sender) {
            (bool success, ) = _beneficiary.call{value: _amountRaised}("");
            if (!success) {
                _safeWithdrawal = false;
            } else {
                emit FundTransfer(_beneficiary, _amountRaised, false);
            }
        }
    }

    function contribute() public payable {
        _balanceOf[msg.sender] += msg.value;
        _amountRaised += msg.value;
        // Assuming _tokenReward is a contract implementing a function `transfer(address,uint256)`
        (bool success, ) = _tokenReward.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, msg.value / _price));
        require(success, "Token transfer failed");
        emit FundTransfer(msg.sender, msg.value, true);
    }
}
```

Please note that this code makes some assumptions about the behavior of the `_tokenReward` address. If `_tokenReward` is a contract with a different interface, you may need to adjust the `contribute` function.

Also, the code doesn't include a constructor to initialize variables like `_fundingGoal`, `_deadline`, `_beneficiary`, `_price`, and `_tokenReward`. You should add a constructor that properly initializes these variables.

