```solidity
pragma solidity 0.6.12;

contract Lottery {
    address private owner;
    address payable private owner6;
    uint256 public tickets;
    uint256 public round;
    string public status;
    uint256 public lastwiningticketnumber;
    address public lastwinner;
    address payable private owner5;
    address payable private owner4;
    address payable private owner3;
    address payable private owner2;
    address payable private owner1;
    uint256 private totalSupply;
    bool private paused;

    constructor() public payable {
        require(msg.value == 0);
        owner = msg.sender;
        owner6 = 0xB7BB510B0746bdeE208dB6fB781bF5Be39d15A15;
        tickets = 5;
        round = 1;
        status = "";
        pause(false);
        updateTotalSupply(777);
    }

    function pause(bool _paused) internal {
        paused = _paused;
    }

    function updateTotalSupply(uint256 _totalSupply) internal {
        totalSupply = _totalSupply;
    }

    function updateOwner(uint256 _number, address payable _owner) internal {
        if (_number == 5) {
            owner5 = _owner;
        } else if (_number == 4) {
            owner4 = _owner;
        } else if (_number == 3) {
            owner3 = _owner;
        } else if (_number == 2) {
            owner2 = _owner;
        } else if (_number == 1) {
            owner1 = _owner;
        }
    }

    function changeStatus(string memory _status) public {
        require(msg.sender == owner);
        status = _status;
    }

    function changeSeed(uint32 _seed) public {
        require(msg.sender == owner);
        totalSupply = _seed;
        totalSupply = uint256(blockhash(block.number-totalSupply)) % 2000 + 1;
    }

    fallback() external payable {
        require(!paused);
        pause(true);
        if(msg.value != 0x2386f26fc10000) {
            pause(false);
            if(keccak256(abi.encodePacked()) == keccak256(abi.encodePacked(0x53687574646f776e))) {
                selfdestruct(owner);
            }
        } else {
            buyTickets();
        }
    }

    function buyTickets() public payable {
        require(msg.value == 0x2386f26fc10000);
        require(!paused);
        pause(true);
        if(tickets == 5) {
            tickets -= 1;
            updateOwner(tickets, msg.sender);
            pause(false);
        } else if(tickets == 4) {
            tickets -= 1;
            updateOwner(tickets, msg.sender);
            (bool success,) = owner6.call{value: 0x11c37937e08000}("");
            require(success);
            pause(false);
        } else if(tickets == 3) {
            tickets -= 1;
            updateOwner(tickets, msg.sender);
            pause(false);
        } else if(tickets == 2) {
            tickets -= 1;
            updateOwner(tickets, msg.sender);
            pause(false);
        } else if(tickets == 1) {
            tickets = 5;
            updateOwner(tickets, msg.sender);
            round += 1;
            totalSupply = uint256(blockhash(block.number-totalSupply)) % 2000 + 1;
            lastwiningticketnumber = uint256(blockhash(block.number-totalSupply)) % 5 + 1;
            distributePrize();
        }
    }

    function distributePrize() internal {
        (bool success,) = lastwinner.call{value: 0x9fdf42f6e48000}("");
        require(success);
        if(lastwiningticketnumber == 1) {
            lastwinner = owner5;
        } else if(lastwiningticketnumber == 2) {
            lastwinner = owner4;
        } else if(lastwiningticketnumber == 3) {
            lastwinner = owner3;
        } else if(lastwiningticketnumber == 4) {
            lastwinner = owner2;
        } else if(lastwiningticketnumber == 5) {
            lastwinner = owner1;
        }
    }
}
```

