Here is a simple smart contract based on your description. Please note that complex business logic can lead to errors and security issues, so a thorough audit is recommended before deploying the contract.

```solidity
pragma solidity 0.6.12;

contract YourContract {
    mapping(address => uint256) public checkinvestments;
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) public checkwithdrawals;
    mapping(address => uint256) public checkreferral;
    uint256 public step;
    uint256 public minimum;
    uint256 public stakingrequirement;
    address payable public ownerwallet;
    address public owner;

    constructor() public payable {
        step = 100;
        minimum = 0x2386f26fc10000;
        stakingrequirement = 0x1bc16d674ec80000;
        if(msg.value == 0) {
            owner = msg.sender;
            ownerwallet = msg.sender;
        }
    }

    function transferOwnership(address newOwner, address newOwnerWallet) external {
        require(msg.sender == owner && newOwner != address(0), "Only owner can transfer ownership");
        owner = newOwner;
        ownerwallet = payable(newOwnerWallet);
    }

    function bounty() external {
        require(checkreferral[msg.sender] >= minimum && checkreferral[msg.sender] < address(this).balance, "Invalid bounty request");
        uint256 amount = checkreferral[msg.sender];
        checkreferral[msg.sender] = 0;
        msg.sender.transfer(amount);
    }

    fallback() external payable {
        require(msg.value >= minimum, "Not enough Ether provided.");
        if(checkinvestments[msg.sender] <= 0) {
            checkinvestments[msg.sender] += msg.value;
        } else {
            checkinvestments[msg.sender] += msg.value;
            checkwithdrawals[msg.sender] = 0;
        }
        balanceOf[msg.sender] = block.timestamp;
        ownerwallet.transfer(msg.value * 5 / 100);
    }

    function buy(address referrer) external payable {
        require(msg.value >= minimum, "Not enough Ether provided.");
        if(checkinvestments[msg.sender] <= 0) {
            checkinvestments[msg.sender] += msg.value;
        } else {
            checkinvestments[msg.sender] += msg.value;
            checkwithdrawals[msg.sender] = 0;
        }
        balanceOf[msg.sender] = block.timestamp;
        ownerwallet.transfer(msg.value * 5 / 100);

        if(checkinvestments[referrer] >= stakingrequirement && referrer != address(0)) {
            checkreferral[referrer] += msg.value * 5 / 100;
        }
    }

    function withdraw() external {
        require(balanceOf[msg.sender] > 0, "No balance to withdraw");
        uint256 amount = checkwithdrawals[msg.sender];
        checkwithdrawals[msg.sender] = 0;
        msg.sender.transfer(amount);
    }
}
```

This contract is simplified and may not cover all your needs. It's recommended to perform a thorough audit before deploying it.
