pragma solidity ^0.8.0;

contract Proxy {
    address private _admin;
    address private _fallback;

    event AdminChanged(address indexed previousAdmin, address indexed newAdmin);
    event Upgraded(address indexed newImplementation);

    function implementation() public view returns (address) {
        require(msg.sender == _admin, "Not admin");
        return _fallback;
    }

    function changeAdmin(address newAdmin) public {
        require(msg.sender == _admin, "Not admin");
        require(newAdmin != address(0), "New admin is zero address");
        emit AdminChanged(_admin, newAdmin);
        _admin = newAdmin;
    }

    function admin() public view returns (address) {
        require(msg.sender == _admin, "Not admin");
        return _admin;
    }

    fallback() external payable {
        require(msg.sender != _admin, "Cannot call fallback function from the proxy admin");
        (bool success,) = _fallback.delegatecall(msg.data);
        require(success, "Delegate call failed");
    }

    function upgradeTo(address newFallback) public {
        require(msg.sender == _admin, "Not admin");
        require(newFallback != address(0), "New implementation is zero address");
        require(isContract(newFallback), "Cannot set a proxy implementation to a non-contract address");
        _fallback = newFallback;
        emit Upgraded(newFallback);
    }

    function upgradeToAndCall(address newFallback, bytes memory data) public payable {
        require(msg.sender == _admin, "Not admin");
        require(isContract(newFallback), "Cannot set a proxy implementation to a non-contract address");
        _fallback = newFallback;
        emit Upgraded(newFallback);
        (bool success,) = newFallback.delegatecall(data);
        require(success, "Delegate call failed");
    }

    function isContract(address addr) private view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }
}
```

This contract is a proxy contract which is used to delegate calls to other contracts (_fallback). It has functionality to change the admin (_admin) and to upgrade the contract it delegates to (_fallback). The fallback function is used to forward all calls not caught by the function signatures to the fallback address. The `upgradeToAndCall` function is used to upgrade the contract it delegates to and immediately make a call to the new contract. The `isContract` function is used to check if a given address contains code, which means it is a contract.

