Sure, here's a simple contract that implements these requirements:

```solidity
pragma solidity 0.6.12;

contract TradingContract {

    address public owner;
    bool public safeguard;
    address public newowner;
    address public admin;
    address payable public feeaccount;
    uint256 public tradingfee;
    mapping (address => uint256) public tokens;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderfills;

    constructor() public payable {
        tradingfee = 50;
        if(msg.value == 0){
            owner = msg.sender;
            feeaccount = msg.sender;
        }
    }

    function changeTradingFee(uint256 _tradingfee) public {
        require(msg.sender == owner);
        tradingfee = _tradingfee;
    }

    function changeFeeAccount(address payable _feeaccount) public {
        require(msg.sender == owner);
        feeaccount = _feeaccount;
    }

    function order(address _tokenGet, uint256 _amountGet, address _tokenGive, uint256 _amountGive, uint256 _expires, uint256 _nonce) public {
        bytes32 _hash = keccak256(abi.encodePacked(this, _tokenGet, _amountGet, _tokenGive, _amountGive, _expires, _nonce));
        orders[msg.sender][_hash] = 1;
    }

    function deposit() public payable {
        tokens[msg.sender] = tokens[msg.sender] + msg.value;
    }

    function acceptOwnership() public {
        require(msg.sender == newowner);
        owner = newowner;
        newowner = address(0);
    }

    function onlyOwnerTransferOwnership(address _newowner) public {
        require(msg.sender == owner);
        newowner = _newowner;
    }

    function changeSafeguardStatus() public {
        require(msg.sender == owner);
        safeguard = !safeguard;
    }

    function withdraw(uint256 _amount) public {
        require(!safeguard && tokens[msg.sender] >= _amount);
        tokens[msg.sender] -= _amount;
        msg.sender.transfer(_amount);
    }

    // ... other functions are not included for brevity ...

}
```

This contract is quite complex and has many functions, so I've omitted some for brevity. This code sample should give you a good start, though. You can fill in the rest of the functions following the pattern demonstrated in the sample.
