```solidity
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

contract Game {
    mapping(address => uint256) private balanceOf1;
    mapping(address => uint256) private balanceOf;
    address private owner;
    address private owner1;
    uint256 public currentbetlimit;
    uint256 public currentdifficulty;
    bool private decimals;
    uint256 private totalSupply;

    constructor() public {
        owner = msg.sender;
        owner1 = msg.sender;
        decimals = false;
        currentdifficulty = 1;
        currentbetlimit = 1;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    function AdjustDifficulty(uint256 difficulty) public onlyOwner {
        currentdifficulty = difficulty;
    }

    function OpenToThePublic() public onlyOwner {
        decimals = true;
    }

    function wager() public payable {
        require(msg.sender == tx.origin, "Must be external owned account");
        require(msg.value == currentbetlimit, "Ether not equal to current bet limit");
        require(decimals, "Decimals must be true");
        balanceOf1[msg.sender] = block.number;
        balanceOf[msg.sender] = msg.value;
    }

    function AdjustBetAmounts(uint256 betAmount) public onlyOwner {
        currentbetlimit = betAmount;
    }

    function donate() public payable {
        require(decimals, "Decimals must be true");
        (bool success, ) = owner1.call{value: msg.value}("");
        require(success, "Transfer failed");
        totalSupply += msg.value;
    }

    function transferAnyERC20Token(address tokenAddress, address to, uint256 amount) public onlyOwner {
        (bool success, ) = tokenAddress.call(abi.encodeWithSignature("transfer(address,uint256)", to, amount));
        require(success, "Transfer failed");
    }

    function play() public {
        require(msg.sender == tx.origin, "Must be external owned account");
        require(balanceOf[msg.sender] > 0, "No balance to play");
        require(balanceOf1[msg.sender] < block.number, "Balance1 must be smaller than current block number");
        require(decimals, "Decimals must be true");
        balanceOf1[msg.sender] = 0;
        balanceOf[msg.sender] = 0;
        bytes32 hash = keccak256(abi.encodePacked(blockhash(balanceOf1[msg.sender]), uint256(0), uint256(0x77d), uint256(0x69b), uint256(20), uint256(-1), uint256(0)));
        uint256 result = uint256(hash) % currentdifficulty + 1;
        if (result != currentdifficulty / 2) {
            (bool success, ) = owner1.call{value: currentbetlimit / 2}("");
            require(success, "Transfer failed");
            totalSupply += currentbetlimit / 2;
        }
        if (result == currentdifficulty / 2) {
            (bool success, ) = msg.sender.call{value: address(this).balance / 2}("");
            require(success, "Transfer failed");
        }
    }
}
```
