```solidity
pragma solidity 0.6.12;

contract Contract {
    address public owner;
    address public escapecaller;
    address payable public escapedestination;
    uint256[] public numberofauthorizedpayments;
    address public securityguard;
    uint256 public absolutemintimelock;
    uint256 public timelock;
    uint256 public maxsecurityguarddelay;
    mapping (address => bool) public allowedspenders;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    modifier onlyEscapeCaller {
        require(msg.sender == escapecaller);
        _;
    }

    function setTimelock(uint256 newTimelock) public onlyOwner payable {
        require(newTimelock >= absolutemintimelock);
        timelock = newTimelock;
    }

    function escapeHatch() public onlyEscapeCaller payable {
        escapedestination.transfer(address(this).balance);
    }

    function changeEscapeCaller(address newEscapeCaller) public onlyEscapeCaller payable {
        escapecaller = newEscapeCaller;
    }

    function authorizeSpender(address spender, bool state) public onlyOwner payable {
        allowedspenders[spender] = state;
    }

    function cancelPayment(uint256 paymentID) public onlyOwner payable {
        require(paymentID < numberofauthorizedpayments.length);
        require(numberofauthorizedpayments[6*paymentID] == 0);
        numberofauthorizedpayments[paymentID] = 1;
    }

    function changeOwner(address newOwner) public onlyOwner payable {
        owner = newOwner;
    }

    function delayPayment(uint256 paymentID, uint256 delay) public payable {
        require(msg.sender == securityguard);
        require(paymentID < numberofauthorizedpayments.length);
        require(numberofauthorizedpayments[6*paymentID] == 0);
        require(numberofauthorizedpayments[6*paymentID] + delay <= maxsecurityguarddelay);
        numberofauthorizedpayments[paymentID] = delay + numberofauthorizedpayments[6*paymentID];
    }

    function setSecurityGuard(address newSecurityGuard) public onlyOwner payable {
        securityguard = newSecurityGuard;
    }

    function setMaxSecurityGuardDelay(uint256 newMaxSecurityGuardDelay) public onlyOwner payable {
        maxsecurityguarddelay = newMaxSecurityGuardDelay;
    }

