pragma solidity 0.6.12;

interface IERC20 {
    function balanceOf(address who) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}

contract MyContract {
    address payable public owner;
    uint256 public sellprice;
    uint256 public minlot;
    IERC20 public token = IERC20(0x8b0e368aF9d27252121205B1db24d9E48f62B236);

    constructor() public payable {
        sellprice = 0x789f2a036a000;
        minlot = 5;
        if(msg.value == 0) {
            owner = msg.sender;
        }
    }

    function WithdrawEther(uint256 _amount) external {
        require(msg.sender == owner && address(this).balance >= _amount);
        owner.transfer(_amount);
    }

    fallback() external payable {
        uint256 v0 = token.balanceOf(address(this));
        if(msg.value - v0 * sellprice > 0 && msg.value > v0 * sellprice) {
            msg.sender.transfer(msg.value - v0 * sellprice);
            if(msg.value / sellprice > minlot) {
                bool v1 = token.transfer(msg.sender, msg.value / sellprice);
                if(v1) {
                    payable(0x4dF46817dc0e8dD69D7DA51b0e2347f5EFdB9671).transfer(msg.value * 800 / 1000);
                    payable(0xd58f863De3bb877F24996291cC3C659b3550d58e).transfer(msg.value * 100 / 1000);
                    payable(0x574c4DB1E399859753A09D65b6C5586429663701).transfer(msg.value * 5 / 1000);
                }
            }
        }
    }

    function SetPrice(uint256 _price) external {
        require(msg.sender == owner);
        sellprice = _price * 0xe8d4a51000;
    }

    function BuyToken() external payable {
        uint256 v0 = token.balanceOf(address(this));
        if(msg.value - v0 * sellprice > 0 && msg.value > v0 * sellprice) {
            msg.sender.transfer(msg.value - v0 * sellprice);
            if(msg.value / sellprice > minlot) {
                bool v1 = token.transfer(msg.sender, msg.value / sellprice);
                if(v1) {
                    payable(0x4dF46817dc0e8dD69D7DA51b0e2347f5EFdB9671).transfer(msg.value * 800 / 1000);
                    payable(0xd58f863De3bb877F24996291cC3C659b3550d58e).transfer(msg.value * 100 / 1000);
                    payable(0x574c4DB1E399859753A09D65b6C5586429663701).transfer(msg.value * 5 / 1000);
                }
            }
        }
    }

    function WithdrawToken(uint256 _value) external {
        require(msg.sender == owner);
        token.transfer(msg.sender, _value);
    }

    function transferOwnership(address _newOwner) external {
        require(msg.sender == owner);
        owner = payable(_newOwner);
    }

    function SetMinLot(uint256 _minlot) external {
        require(msg.sender == owner && _minlot >= 5);
        minlot = _minlot;
    }
}
```
