```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    mapping (address => uint256) public balanceof;
    uint256 public maxsupply;
    uint256 public totalsupply;
    mapping (address => mapping (address => uint256)) public allowance;
    string public symbol;
    string public name;
    string public version;
    uint8 public decimals;
    uint256 private totalSupply2;
    uint256 public buyprice;
    uint256 public sellprice;
    uint256 public contracteth;
    bool public allowsell;
    bool public allowbuy;

    constructor() public {
        owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function transferEther(address payable _to, uint256 _value) public {
        require(msg.sender == owner && _value <= contracteth, "Not allowed");
        _to.transfer(_value);
        contracteth -= _value;
    }

    function reclaimToken(address _token) public {
        require(msg.sender == owner, "Not allowed");
        (bool success, bytes memory data) = _token.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
        require(success && data.length >= 32, "Call failed");
        uint256 v0 = abi.decode(data, (uint256));
        (success, ) = _token.call(abi.encodeWithSignature("transfer(address,uint256)", owner, v0));
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        uint256 oldValue = allowance[msg.sender][_spender];
        if (_value > oldValue) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] = oldValue - _value;
        }
    }

    // ... other functions omitted for brevity ...
}
```

