Here is the solidity smart contract code that meets the given specifications:

```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    mapping(address => uint256) public deposited;
    uint8 public state;
    address payable public wallet;

    modifier onlyOwner(){
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    modifier checkState(uint8 _state){
        require(state == _state, "Invalid state");
        _;
    }

    constructor(address payable _wallet) public {
        owner = msg.sender;
        wallet = _wallet;
        state = 0;
    }

    function close() onlyOwner checkState(0) public {
        state = 2;
        (bool success, ) = wallet.call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    function enableRefunds() onlyOwner checkState(0) public {
        state = 1;
    }

    function transferOwnership(address newOwner) onlyOwner public {
        require(newOwner != address(0), "New owner is the zero address");
        owner = newOwner;
    }

    function refund(address payable _to) onlyOwner checkState(1) public {
        require(_to != address(0), "Can't refund to the zero address");
        uint256 amount = deposited[_to];
        deposited[_to] = 0;
        (bool success, ) = _to.call{ value: amount }("");
        require(success, "Refund failed");
    }

    function deposit(address _to) onlyOwner checkState(0) payable public {
        require(_to != address(0), "Can't deposit to the zero address");
        uint256 amount = msg.value;
        require(amount > 0, "Deposit amount must be greater than 0");
        deposited[_to] += amount;
    }
}
```
Please note that this contract does not include arithmetic operation, so we don't need to import SafeMath library for checking overflows/underflows. However, if the contract needs to make arithmetic operations, the SafeMath library should be used.
