Here is the Solidity contract code according to your specifications:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    address private token;
    uint256 public baseprice;
    uint256 public basediscount;

    constructor() public {
        owner = msg.sender;
    }

    function setOwner(address _owner) public {
        require(_owner != address(0), "Owner address cannot be 0");
        owner = _owner;
    }

    function withdraw(uint256 amount) public {
        require(amount <= address(this).balance, "Not enough balance");
        require(msg.sender == owner, "Only owner can withdraw");
        payable(owner).transfer(amount);
    }

    function withdrawAll() public {
        require(address(this).balance <= address(this).balance, "Not enough balance");
        require(msg.sender == owner, "Only owner can withdraw");
        payable(owner).transfer(address(this).balance);
    }

    function purchase(uint16 _amount) public payable {
        uint256 totalPrice = _amount * baseprice;
        uint256 totalDiscount = _amount * basediscount;
        uint256 totalValue = totalPrice + totalDiscount;

        require(totalValue <= address(this).balance, "Not enough balance");
        require(msg.value >= totalPrice, "Not enough Ether for purchase");

        (bool success, ) = token.call(abi.encodePacked(bytes4(keccak256("purchaseFor(address,uint16,address)")), msg.sender, _amount, address(this)));
        require(success, "External call failed");
    }
}
```
Please note that this code assumes that the `purchaseFor` function in the external contract `token` has the signature `purchaseFor(address,uint16,address)`. Also, the code doesn't include any overflow or underflow checks for arithmetic operations. You may need to use a safe math library to handle potential overflow or underflow problems.
