```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    address public newowner;
    uint256 public tosnipe;

    constructor() public payable {
        owner = 0x0B0eFad4aE088a88fFDC50BCe5Fb63c6936b9220;
    }

    function sacUp() public payable {
        require((msg.value + tosnipe) >= tosnipe);
        tosnipe = msg.value + tosnipe;
    }

    function fetchBalance() public {
        require(msg.sender == owner && tosnipe <= address(this).balance);
        msg.sender.transfer(address(this).balance - tosnipe);
    }

    function fetchvault() public {
        (bool success, ) = 0x31cF8B6E8bB6cB16F23889F902be86775bB1d0B3.call(
            abi.encodeWithSignature("vaultToWallet(address)", address(this))
        );
        require(success);
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        newowner = _newOwner;
        owner = newowner;
    }

    function unfetchedVault() public {
        (bool success, ) = 0x31cF8B6E8bB6cB16F23889F902be86775bB1d0B3.call(
            abi.encodeWithSignature("myEarnings()")
        );
        require(success);
    }

    function sacUpto(address _address) public payable {
        require(0x16345785d8a0000 <= tosnipe && 0x16345785d8a0000 < tosnipe);
        tosnipe -= 0x16345785d8a0000;
        (bool success, ) = 0x31cF8B6E8bB6cB16F23889F902be86775bB1d0B3.call{value: 0x16345785d8a0000}(
            abi.encodeWithSignature("sendInSoldier(address,uint256)", _address, 1)
        );
        require(success);
    }
}
```

