```solidity
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    address public newowner;
    string public standard;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;

    constructor() public {
        owner = msg.sender;
    }

    function approve(address _to, uint256 _value) public {
        require(_to != address(0), "Zero address");

        if (_value == 0) {
            allowance[msg.sender][_to] = 0;
        } else if (allowance[msg.sender][_to] == 0) {
            allowance[msg.sender][_to] = _value;
        }
    }

    function acceptOwnership() public {
        require(msg.sender == newowner, "Only new owner can call this function");
        owner = newowner;
        newowner = address(0);
    }

    function transferOwnership(address _newowner) public {
        require(msg.sender == owner, "Only owner can call this function");
        require(_newowner != owner, "New owner must be different from current owner");
        newowner = _newowner;
    }

    function withdrawTo(address payable _to, uint256 _value) public {
        require(_to != address(this), "Cannot withdraw to contract address");
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        _to.transfer(_value);
    }

    function withdraw(uint256 _value) public {
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        msg.sender.transfer(_value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0), "Zero address");
        require(_to != address(this), "Cannot transfer to contract address");
        allowance[_from][msg.sender] -= _value;
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0), "Zero address");
        require(_to != address(this), "Cannot transfer to contract address");
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    fallback() external payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function deposit() public payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function withdrawTokens(address _tokenContract, address _to, uint256 _value) public {
        require(_to != address(0), "Zero address");
        require(_to != address(this), "Cannot withdraw to contract address");

        (bool success,) = _tokenContract.call(
            abi.encodeWithSignature("transfer(address,uint256)", _to, _value)
        );
        require(success, "Transfer failed");
    }
}
```

This contract includes all the required functions and state variables. It uses the `fallback` function to receive Ether and update the caller's balance and the total supply. It includes `approve`, `acceptOwnership`, `transferOwnership`, `withdrawTo`, `withdraw`, `transferFrom`, `transfer`, and `deposit` functions. It uses `require` to check conditions and `transfer` to send Ether. It uses `.call()` for external calls and `abi.encodeWithSignature` for function calls to other contracts.
