pragma solidity ^0.6.12;

contract Contract {
    mapping(address => uint256) public isowner;
    mapping(uint256 => mapping(address => uint256)) public confirmations;
    mapping(uint256 => uint256) private allowance1;
    address[] public owners;
    uint256 public required;
    uint256 public transactionids;

    function revokeConfirmation(uint256 arg) external {
        require(isowner[msg.sender] > 0, "Caller is not owner");
        require(confirmations[arg][msg.sender] > 0, "Confirmation does not exist");
        require(allowance1[arg] == 0, "Allowance is not zero");
        confirmations[arg][msg.sender] = 0;
    }

    function removeOwner(address arg) external {
        require(isowner[arg] > 0, "Address is not owner");
        isowner[arg] = 0;
        owners[owners.length-1] = owners[0];
        owners[0] = address(0);
        if (required > owners.length) {
            required = owners.length;
        }
    }

    function addOwner(address arg) external {
        require(isowner[arg] == 0, "Address is already owner");
        require(arg != address(0), "Address is zero");
        require(owners.length+1 != 0, "Owners number overflow");
        require(required <= owners.length+1, "Required is too large");
        require(owners.length+1 <= 50, "Owners number too large");
        require(required != 0, "Required is zero");
        isowner[arg] = 1;
        owners.push(arg);
    }

    function changeRequirement(uint256 arg) external {
        require(owners.length != 0, "No owners");
        require(arg != 0, "Argument is zero");
        require(arg <= owners.length, "Argument is too large");
        require(owners.length <= 50, "Owners number too large");
        required = arg;
    }

    function replaceOwner(address arg1, address arg2) external {
        require(isowner[arg1] > 0, "First address is not owner");
        require(isowner[arg2] == 0, "Second address is already owner");
        isowner[arg1] = 0;
        isowner[arg2] = 1;
        for (uint256 i = 0; i < owners.length; i++) {
            if (owners[i] == arg1) {
                owners[i] = arg2;
                break;
            }
        }
    }

    function confirmTransaction(uint256 arg) external {
        require(isowner[msg.sender] > 0, "Caller is not owner");
        require(allowance1[arg] != 0, "Allowance is zero");
        require(confirmations[arg][msg.sender] == 0, "Confirmation already exists");
        confirmations[arg][msg.sender] = 1;
        if (allowance1[arg] == 0 && owners.length > 0 && required == 0) {
            allowance1[arg] = 1;
            payable(msg.sender).call{value: allowance1[arg]}("");
            allowance1[arg] = 0;
        }
    }

    function executeTransaction(uint256 arg) external {
        require(isowner[msg.sender] > 0, "Caller is not owner");
        require(confirmations[arg][msg.sender] > 0, "Confirmation does not exist");
        require(allowance1[arg] == 0, "Allowance is not zero");
        require(owners.length > 0 && required == 0, "Contract conditions not met");
        allowance1[arg] = 1;
        payable(msg.sender).call{value: allowance1[arg]}("");
        allowance1[arg] = 0;
    }
}
