Here is the Solidity code based on your specifications:

```solidity
pragma solidity ^0.6.12;

contract TokenSale {
    address public owner;
    string public name;
    address payable public beneficiary;
    uint256 public starttime;
    uint256 public duration;
    uint256 public tokenscontractbalance;
    uint256 public price;
    uint256 public discountprice;
    address public tokenreward;
    mapping(address => uint256) public balanceof;
    mapping(address => uint8) public whitelist;
    uint8 public crowdsaleclosed;
    uint256 public tokenownernumber;
    bool public halted;

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function joinWhiteList(address _address) public onlyOwner {
        require(_address != address(0));
        whitelist[_address] = 1;
    }

    function halt() public onlyOwner {
        halted = true;
    }

    function finalizeSale() public onlyOwner {
        require(crowdsaleclosed == 0);
        crowdsaleclosed = 1;
    }

    receive() external payable {
        require(whitelist[msg.sender] == 1 && tokenscontractbalance > 0 && block.timestamp >= starttime && 
                block.timestamp <= starttime + duration && halted == false && msg.value >= 0x15af1d78b58c40000 && 
                crowdsaleclosed == 0);
        uint256 amount = msg.value / price;
        require(amount <= tokenscontractbalance);
        balanceof[msg.sender] += msg.value;
        if(tokenownernumber < 120 && balanceof[msg.sender] == 0){
            balanceof[msg.sender] += msg.value;
        }
        (bool success, ) = tokenreward.call(abi.encodePacked("transfer", msg.sender, amount));
        require(success);
        tokenscontractbalance -= amount;
    }

    function setPrice(uint256 _price) public onlyOwner {
        require(_price > 0);
        price = _price;
    }

    function fundWithdrawal(uint256 _amount) public onlyOwner {
        require(_amount > 0 && address(this).balance >= _amount);
        beneficiary.transfer(_amount);
    }

    function tokenWithdrawal(uint256 _amount) public onlyOwner {
        require(_amount > 0 && tokenscontractbalance >= _amount);
        (bool success, ) = tokenreward.call(abi.encodePacked("transfer", beneficiary, _amount));
        require(success);
        tokenscontractbalance -= _amount;
    }

    function unhalt() public onlyOwner {
        require(halted == true);
        halted = false;
    }

    function setDiscount(uint256 _discountprice) public onlyOwner {
        require(_discountprice > 0);
        discountprice = _discountprice;
    }

    function changeBeneficiary(address payable _newBeneficiary) public onlyOwner {
        require(_newBeneficiary != address(0));
        beneficiary = _newBeneficiary;
    }

    function reopenSale() public onlyOwner {
        crowdsaleclosed = 0;
    }
}
```
