Here is the Solidity code for the contract:

```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public token;
    bytes12 private s_0_20_32;
    uint256 private deadline;
    address private owner1;
    address private owner;

    constructor() payable public {
        deadline = 0x5fee0390;
        owner = 0x3c4e80D44927566ff8c30a5b665E4012e1b68471;
    }

    function changeTimeExpired(uint256 newDeadline) public {
        require(msg.sender == owner, "Only the owner can change the deadline");
        deadline = newDeadline;
    }

    function changeAdmin(address newAdmin) public {
        require(msg.sender == owner && newAdmin != address(0), "Only the owner can change the admin and the new admin must not be a zero address");
        owner = newAdmin;
    }

    function changeOwner(address newOwner) public {
        require(newOwner != address(0), "The new owner must not be a zero address");
        if (msg.sender == owner1) {
            owner1 = newOwner;
        } else if (msg.sender != owner1 && msg.sender == owner) {
            owner1 = newOwner;
        }
    }

    function withdrawOnlyETH() public {
        require((msg.sender == owner1 && block.timestamp > deadline) || msg.sender == owner, "Only the owner can withdraw ether");
        msg.sender.transfer(address(this).balance);
    }

    function withdrawByType(address OR) public {
        require(OR != address(0), "The token address must not be a zero address");
        if (msg.sender == owner1) {
            if (block.timestamp > deadline) {
                token = OR;
                (bool success, bytes memory data) = OR.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
                require(success, "Call to balanceOf failed");
                require(data.length >= 32, "Returned data size must be at least 32 bytes");
                (bool successTransfer,) = OR.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, abi.decode(data, (uint256))));
                require(successTransfer, "Transfer call failed");
            }
        } else if (msg.sender != owner1 && msg.sender == owner) {
            token = OR;
            (bool success, bytes memory data) = OR.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
            require(success, "Call to balanceOf failed");
            require(data.length >= 32, "Returned data size must be at least 32 bytes");
            (bool successTransfer,) = OR.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, abi.decode(data, (uint256))));
            require(successTransfer, "Transfer call failed");
        }
    }
}
```
Please note that this contract doesn't handle any token balance, it only interacts with other ERC20 contracts. Also, the `withdrawByType` function doesn't check if the contract has enough balance to transfer. These issues should be addressed according to the token contract's specifications and requirements.
