```Solidity
pragma solidity 0.6.12;

contract YourContract {
    uint256 public burnprice;
    uint256 public addtoreserve;
    uint256 public emissionprice;
    uint256 private deadline;
    address public owner;
    bool public emissionlocked;
    address public manager;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    address private owner2;
    address private owner3;

    constructor() public payable {
        burnprice = 0;
        emissionlocked = false;
        if (msg.value == 0 && burnprice == 0) {
            owner = msg.sender;
            manager = owner;
        }
    }

    function approve(address _to, uint256 _value) public {
        allowance[msg.sender][_to] = _value;
    }

    function unlockEmission() public {
        require(msg.sender == owner, "Only owner can unlock emission");
        emissionlocked = false;
    }

    function lockEmission() public {
        require(msg.sender == owner, "Only owner can lock emission");
        emissionlocked = true;
    }

    function acceptOwnership() public {
        require(msg.sender == owner2, "Only new owner can accept ownership");
        owner = owner2;
        owner2 = address(0);
    }

    function changeManager(address _newManager) public {
        require(msg.sender == owner, "Only owner can change manager");
        owner3 = _newManager;
    }

    function changeOwner(address _newOwner) public {
        require(msg.sender == owner, "Only owner can change owner");
        owner2 = _newOwner;
    }

    fallback() external payable {
        require(!emissionlocked && addtoreserve > 0 && emissionprice > addtoreserve && msg.value > 0 && balanceof[msg.sender] + msg.value / emissionprice > balanceof[msg.sender]);

        balanceof[msg.sender] += msg.value / emissionprice;
        burnprice += msg.value / emissionprice;
        addtoreserve = address(this).balance / burnprice;
        emissionprice = addtoreserve * 2;
        payable(manager).transfer(msg.value / 2);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(balanceof[_from] >= _value && _value > 0 && allowance[_from][msg.sender] >= _value && _to != address(this) && balanceof[_to] + _value > balanceof[_to]);

        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function burnAll() public {
        addtoreserve = address(this).balance / burnprice;

        require(balanceof[msg.sender] > 0 && balanceof[msg.sender] >= balanceof[msg.sender]);

        burnprice -= balanceof[msg.sender];
        payable(msg.sender).transfer(balanceof[msg.sender] * addtoreserve);
        balanceof[msg.sender] -= balanceof[msg.sender];
        addtoreserve = address(this).balance / burnprice;
    }

    function transferAnyERC20Token(address _tokenAddress, uint256 _amount) public {
        require(msg.sender == owner, "Only owner can transfer ERC20 tokens");

        (bool success, ) = _tokenAddress.call(abi.encodeWithSignature("transfer(address,uint256)", owner, _amount));
        require(success, "Transfer failed");
    }

    function acceptManagership() public {
        require(msg.sender == owner3, "Only new manager can accept managership");

        manager = owner3;
        owner3 = address(0);
    }

    function transfer(address _to, uint256 _value) public {
        if (_to == address(this)) {
            addtoreserve = address(this).balance / burnprice;

            require(balanceof[msg.sender] >= _value && _value > 0);

            balanceof[msg.sender] -= _value;
            burnprice -= _value;
            payable(msg.sender).transfer(_value * addtoreserve);
            addtoreserve = address(this).balance / burnprice;
        } else {
            require(balanceof[_to] + _value > balanceof[_to] && balanceof[msg.sender] >= _value && _value > 0);

            balanceof[msg.sender] -= _value;
            balanceof[_to] += _value;
        }
    }

    function addToReserve() public payable {
        require(msg.value > 0);

        addtoreserve = address(this).balance / burnprice;
        emissionprice = addtoreserve * 2;
    }

    function NoxonInit() public payable {
        require(burnprice == 0 && msg.value > 0 && deadline == 0 && msg.sender == owner);

        balanceof[owner] = 1;
        burnprice = balanceof[owner];
        addtoreserve = msg.value;
        emissionprice = addtoreserve * 2;
        deadline = block.timestamp;
    }
}
