Based on the description provided, here's the Solidity code for the contract:

```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping (address => uint256)) public allowance;
    uint256 public buyprice;

    constructor() public payable {
        owner = msg.sender;
        decimals = 18;
        buyprice = 1;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    fallback() external payable {
        msg.sender.transfer(msg.value);
        if ((balanceof[msg.sender] + (buyprice * msg.value) > balanceof[msg.sender]) && (buyprice * msg.value <= balanceof[owner]) && msg.sender != address(0)) {
            balanceof[owner] -= buyprice * msg.value;
            balanceof[msg.sender] += buyprice * msg.value;
        }
    }

    function selfdestructs() public {
        require(msg.sender == owner);
        selfdestruct(msg.sender);
    }

    function burn(uint256 _value) public {
        require (_value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require (_value <= allowance[_from][msg.sender]);
        if (_value <= balanceof[_from] && _to != address(0) && balanceof[_to] + _value > balanceof[_to]) {
            balanceof[_from] -= _value;
            balanceof[_to] += _value;
            allowance[_from][msg.sender] -= _value;
        }
    }

    function burnFrom(address _from, uint256 _value) public {
        require (_value <= balanceof[_from] && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
    }

    function setPrices(uint256 _buyprice) public {
        require(msg.sender == owner);
        buyprice = _buyprice;
    }

    function transferOwnership(address _owner) public {
        require(msg.sender == owner);
        owner = _owner;
    }

    function buy() public payable {
        if ((balanceof[msg.sender] + (msg.value / buyprice) > balanceof[msg.sender]) && (msg.value / buyprice <= balanceof[address(this)]) && msg.sender != address(0) && buyprice != 0) {
            balanceof[address(this)] -= msg.value / buyprice;
            balanceof[msg.sender] += msg.value / buyprice;
        }
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _value <= balanceof[msg.sender] && balanceof[_to] + _value > balanceof[_to]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public {
        allowance[msg.sender][_spender] = _value;
        if (_extraData.length >= 0) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,address,bytes)", msg.sender, _value, this, _extraData));
            require(success);
        }
    }
}
```

Please note that some of the provided logic has been simplified for readability and to avoid redundancy. For instance, checks in the `transfer` and `transferFrom` functions have been optimized and the `approveAndCall` function has been simplified.

The code also assumes that the balances are in wei, and the buy price is also in wei per token. 

Keep in mind that this is a simple example and real-world contracts require more checks and tests to ensure security and correctness.
