```solidity
pragma solidity 0.6.12;

contract MyContract {
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalsupply;
    string public name;
    uint8 public decimals;
    string public symbol;
    string public version;
    uint256 public unitsoneethcanbuy;
    uint256 public totalethinwei;
    address payable public fundswallet;

    constructor() public {
        // Initialize state variables here
    }

    receive() external payable {
        require(msg.value > 0, "Need to send some Ether");
        totalethinwei = totalethinwei + msg.value;
        require(balanceof[fundswallet] >= msg.value * unitsoneethcanbuy, "Insufficient funds");
        balanceof[fundswallet] = balanceof[fundswallet] - msg.value * unitsoneethcanbuy;
        balanceof[msg.sender] = balanceof[msg.sender] + msg.value * unitsoneethcanbuy;
        fundswallet.transfer(msg.value);
    }

    function approve(address _to, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_to] = _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value > 0 && allowance[_from][msg.sender] >= _value && balanceof[_from] >= _value, "Transfer not allowed");
        balanceof[_to] = balanceof[_to] + _value;
        balanceof[_from] = balanceof[_from] - _value;
        allowance[_from][msg.sender] = allowance[_from][msg.sender] - _value;
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value && _value > 0, "Transfer not allowed");
        balanceof[msg.sender] = balanceof[msg.sender] - _value;
        balanceof[_to] = balanceof[_to] + _value;
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        if (_extraData.length <= 0) {
            (bool success,) = _spender.call(abi.encodePacked(bytes4(keccak256("receiveApproval(address,uint256,address,bytes)")), msg.sender, _value, this, _extraData));
        } else {
            // Add more conditions as per contract requirements
        }
        return true;
    }
}
```

Please note that the function `receiveApproval` needs to be implemented in the contract of `_spender` address. The `approveAndCall` function is incomplete as per the contract description provided, we need more details to add more conditions.
