pragma solidity 0.6.12;

contract YourContract {
    string public name;
    string public symbol;
    uint256 public decimals;
    uint256 public salestart;
    uint256 public saleend;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    address payable private owner;

    constructor() public {
        owner = msg.sender;
        // Initialize other state variables if necessary
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner && address(this).balance > 0x1b1ae4d6e2ef500000 && block.timestamp > saleend);
        owner.transfer(address(this).balance);
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(balanceof[_from] >= _value && balanceof[_to] + _value > balanceof[_to] && block.timestamp > saleend + 0x127500 && _value > 0 && allowance[_from][msg.sender] >= _value);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    fallback() external payable {
        require(msg.value > 0 && block.timestamp > salestart && msg.sender != address(0) && block.timestamp < saleend);
        balanceof[msg.sender] -= (0x1b1ae4d6e2ef500000 * msg.value) / 0xde0b6b3a7640000;
        balanceof[msg.sender] += (0x1b1ae4d6e2ef500000 * msg.value) / 0xde0b6b3a7640000;
    }

    function refund() public {
        require(address(this).balance <= 0x1b1ae4d6e2ef500000 && block.timestamp > saleend);
        msg.sender.transfer(balanceof[msg.sender] / 0xde0b6b3a7640000);
    }

    function buyTokens() public payable {
        require(msg.value > 0 && block.timestamp > salestart && msg.sender != address(0) && block.timestamp < saleend);
        balanceof[msg.sender] -= (0x1b1ae4d6e2ef500000 * msg.value) / 0xde0b6b3a7640000;
        balanceof[msg.sender] += (0x1b1ae4d6e2ef500000 * msg.value) / 0xde0b6b3a7640000;
    }
}
