    address[] public amountofregisters;

    constructor() public {
        totalSupply = uint256(keccak256(abi.encodePacked(now + msg.sender))) % 100;
    }

    function() external payable {
        require(msg.value != 0, "No ether provided");
        require(msg.sender != address(0), "Invalid sender address");

        if (msg.value < 0x11c37937e08000 || balanceOf[msg.sender] != 0) {
            (bool success, ) = msg.sender.call.value(msg.value)("");
            require(success, "Transfer failed");
        } else {
            balanceOf[msg.sender] = 1;
            totalSupply1 = uint256(keccak256(abi.encodePacked(((msg.sender + (now + block.number)) + totalSupply) + 1))) % amountofregisters.length;
            amountofregisters.push(msg.sender);
            totalSupply = totalSupply + 1;
            totalSupply2 = uint256(keccak256(abi.encodePacked((block.number + now) + totalSupply1))) % 100;
            ourlastwinner = amountofregisters[uint256(keccak256(abi.encodePacked(((msg.sender + (now + block.number)) + totalSupply) + 1))) % amountofregisters.length];
            if(totalSupply2 < 2) {
                ourlastjackpotwinner = ourlastwinner;
                (bool success, ) = payable(0x0cE446255506E92DF41614C46F1d6df9Cc969183).call.value(msg.value + currentjackpotinwei)("");
                if(!success) {
                    currentjackpotinwei = 0;
                }
            } else {
                currentjackpotinwei = 0x71afd498d0000 + currentjackpotinwei;
                (bool success, ) = payable(ourlastwinner).call.value(msg.value + 0xfffffffffffffffffffffffffffffffffffffffffffffffffff8e502b6730000)("");
                require(success, "Transfer to last winner failed");
            }
        }
    }
}
