// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Crowdfunding {
    uint256 public _fundingGoal;
    uint256 public _amountRaised;
    uint256 public _deadline;
    uint256 public _price;
    address public _beneficiary;
    mapping (address => uint256) public _balanceOf;
    bool public _safeWithdrawal;
    IERC20 public _tokenReward;

    event FundTransfer(address backer, uint256 amount, bool isContribution);
    event GoalReached(address beneficiary, uint256 amountRaised);

    function checkGoalReached() public {
        require(block.timestamp >= _deadline, "Deadline not reached");
        if (_amountRaised >= _fundingGoal) {
            _safeWithdrawal = true;
            emit GoalReached(_beneficiary, _amountRaised);
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp >= _deadline, "Deadline not reached");
        if (!_safeWithdrawal) {
            uint256 amount = _balanceOf[msg.sender];
            _balanceOf[msg.sender] = 0;
            (bool success, ) = msg.sender.call{value: amount}("");
            require(success, "Transfer failed");
            emit FundTransfer(msg.sender, amount, false);
        }

        if (_safeWithdrawal && _beneficiary == msg.sender) {
            uint256 raisedAmount = _amountRaised;
            _amountRaised = 0;
            (bool success, ) = _beneficiary.call{value: raisedAmount}("");
            require(success, "Transfer failed");
            emit FundTransfer(_beneficiary, raisedAmount, false);
        }
    }

    function contribute() public payable {
        uint256 amount = msg.value;
        _balanceOf[msg.sender] += amount;
        _amountRaised += amount;
        require(_tokenReward.transfer(msg.sender, amount / _price), "Token transfer failed");
        emit FundTransfer(msg.sender, amount, true);
    }
}

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
}
```
