pragma solidity 0.6.12;

contract OrderContract {
    mapping(address => uint256) public admin;
    mapping(address => uint256) private addressOfBountyMembers;
    mapping(address => mapping(address => uint256)) public balanceof;
    mapping(address => mapping(bytes32 => uint256)) public orders;
    mapping(address => mapping(bytes32 => uint256)) public orderfills;

    address public feeaccount;
    uint256 public feemake;
    uint256 public feetake;

    function order(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        orders[msg.sender][v0] = 1;
    }

    function amountFilled(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, address arg7) public {
        sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
    }

    function cancelOrder(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, uint8 arg7, bytes32 arg8, bytes32 arg9) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        address v1;
        if (orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = arg2;
        } else {
            v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32",v0)), arg7, arg8, arg9);
            orderfills[msg.sender][v0] = arg2;
        }
    }

    function changeFeeMake(uint256 arg1) public {
        require(arg1 <= feemake, "Fee is too high");
        require(msg.sender == feeaccount, "Only admin can change this");
        feemake = arg1;
    }

    function withdraw(uint256 arg1) public {
        require(addressOfBountyMembers[msg.sender] >= arg1, "Insufficient funds");
        admin[msg.sender] -= arg1;
        msg.sender.transfer(arg1);
    }

    function changeFeeAccount(address arg1) public {
        require(msg.sender == feeaccount, "Only admin can change this");
        feeaccount = arg1;
    }

    function changeAdmin(address arg1) public {
        require(msg.sender == feeaccount, "Only admin can change this");
        admin[arg1] = admin[msg.sender];
        admin[msg.sender] = 0;
    }

    function changeFeeTake(uint256 arg1) public {
        require(arg1 <= feetake, "Fee is too high");
        require(msg.sender == feeaccount, "Only admin can change this");
        feetake = arg1;
    }

    function depositToken(address arg1, uint256 arg2) public {
        balanceof[arg1][msg.sender] += arg2;
    }

    function testTrade(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, address arg7, uint8 arg8, bytes32 arg9, bytes32 arg10, uint256 arg11, address arg12) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32",v0)), arg8, arg9, arg10);
    }

    function deposit() public payable {
        admin[msg.sender] += msg.value;
    }

    function withdrawToken(address arg1, uint256 arg2) public {
        balanceof[arg1][msg.sender] -= arg2;
    }

    function availableVolume(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, address arg7, uint8 arg8, bytes32 arg9, bytes32 arg10) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32",v0)), arg8, arg9, arg10);
    }
}
