```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public authority;
    address payable public owner;
    uint256 public nameprice;
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) private balanceOf1;

    constructor() public payable {
        owner = msg.sender;
        nameprice = 0x2386f26fc10000;
    }

    function setOwner(address newOwner) public {
        require(newOwner != address(0), "New owner address cannot be 0x0");
        if (msg.sender == address(this)) {
            owner = payable(newOwner);
        } else {
            if (msg.sender == owner) {
                owner = payable(newOwner);
            } else if (authority != address(0)) {
                (bool success, bytes memory result) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff, newOwner, 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff));
                require(success, "Authority call failed");
                uint256 v0 = abi.decode(result, (uint256));
                require(v0 != 0, "Not authorized");
                owner = payable(newOwner);
            }
        }
    }

    function setAuthority(address newAuthority) public {
        require(newAuthority != address(0), "New authority address cannot be 0x0");
        if (msg.sender == address(this)) {
            authority = newAuthority;
        } else {
            if (msg.sender == owner) {
                authority = newAuthority;
            } else if (authority != address(0)) {
                (bool success, bytes memory result) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff, newAuthority, 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff));
                require(success, "Authority call failed");
                uint256 v0 = abi.decode(result, (uint256));
                require(v0 != 0, "Not authorized");
                authority = newAuthority;
            }
        }
    }

    function getMoney() public {
        uint256 balance = address(this).balance;
        require(balance > 0, "Contract balance is 0");
        if (msg.sender == address(this)) {
            owner.transfer(balance);
        } else {
            if (msg.sender == owner) {
                owner.transfer(balance);
            } else if (authority != address(0)) {
                (bool success, bytes memory result) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff, owner, 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff));
                require(success, "Authority call failed");
                uint256 v0 = abi.decode(result, (uint256));
                require(v0 != 0, "Not authorized");
                owner.transfer(balance);
            }
        }
    }
}
```

