        require(msg.sender == freezeContract, "Only freezeContract can set issuer");
        isIssuer[_address] = true;
        emit Appointed(_address);
    }

    function allowance(address _owner, address _spender) public view returns (uint256) { 
        return allowed[_owner][_spender];
    }

    function blacklist(address _address) public notFrozen { 
        require(msg.sender == freezeContract, "Only freezeContract can blacklist");
        blacklisted[_address] = true;
        emit Blacklisted(_address);
    }

    function freezeContract(bool _freeze) public notFrozen { 
        require(msg.sender == freezeContract, "Only freezeContract can freeze contract");
        contractFrozen = _freeze;
    }

    function transfer(address _to, uint256 _value) public notFrozen { 
        require(balances[msg.sender] >= _value, "Insufficient balance");
        require(!blacklisted[msg.sender], "Sender is blacklisted");

        balances[msg.sender] -= _value;
        balances[_to] += _value;

        emit Transfer(msg.sender, _to, _value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public notFrozen { 
        require(balances[_from] >= _value, "Insufficient balance");
        require(allowed[_from][msg.sender] >= _value, "Not allowed to transfer");
        require(!blacklisted[_from], "Sender is blacklisted");

        balances[_from] -= _value;
        balances[_to] += _value;
        allowed[_from][msg.sender] -= _value;

        emit Transfer(_from, _to, _value);
    }

    function approve(address _spender, uint256 _value) public notFrozen { 
        require(!blacklisted[msg.sender], "Owner is blacklisted");

        allowed[msg.sender][_spender] = _value;

        emit Approval(msg.sender, _spender, _value);
    }

