```Solidity
pragma solidity 0.6.12;

contract BidContract {
    address payable public technicalrise;
    address payable public lastbidder;
    uint256 public timeout;
    uint256 public kbalance;
    uint256 public feerate = 100;

    constructor() public payable {
        technicalrise = 0x7c0Bf55bAb08B4C1eBac3FC115C394a739c62538;
        lastbidder = msg.sender;
        kbalance = msg.value;
        timeout = now + 600;
        feerate = 100;
    }

    fallback() external payable {
        kbalance += msg.value - msg.value / feerate;
        (bool success, ) = technicalrise.call{value: msg.value / feerate}("");
        require(success, "Transfer failed.");
        if(msg.value - msg.value / feerate < 0x232bff5f46c000) {
          timeout += 120;
        } else {
          timeout += 0x526f1e874dd20000 / (msg.value - msg.value / feerate);
          lastbidder = msg.sender;
          if(timeout <= now) {
            (success, ) = lastbidder.call{value: kbalance - (msg.value - msg.value / feerate)}("");
            require(success, "Transfer failed.");
            kbalance = msg.value - msg.value / feerate;
            lastbidder = msg.sender;
            timeout = now;
            timeout += 0x526f1e874dd20000 / (msg.value - msg.value / feerate);
          }
        }
    }

    function fund() public payable {
        kbalance += msg.value - msg.value / feerate;
        (bool success, ) = technicalrise.call{value: msg.value / feerate}("");
        require(success, "Transfer failed.");
        if(msg.value - msg.value / feerate < 0x232bff5f46c000) {
          timeout += 120;
        } else {
          timeout += 0x526f1e874dd20000 / (msg.value - msg.value / feerate);
          lastbidder = msg.sender;
          if(timeout <= now) {
            (success, ) = lastbidder.call{value: kbalance - (msg.value - msg.value / feerate)}("");
            require(success, "Transfer failed.");
            kbalance = msg.value - msg.value / feerate;
            lastbidder = msg.sender;
            timeout = now;
            timeout += 0x526f1e874dd20000 / (msg.value - msg.value / feerate);
          }
        }
    }
}
