pragma solidity 0.6.12;

contract MyContract {

    address private owner;
    bool private decimals;

    constructor() public {
        owner = msg.sender;
        decimals = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    function selfDestruct(address payable _to) external onlyOwner {
        require(decimals != false, "Decimals must be non-zero");
        require(_to != address(0), "Cannot send to the zero address");

        selfdestruct(_to);
    }

    function setEtheraffle(address _newOwner) external onlyOwner {
        require(_newOwner != address(0), "New owner cannot be the zero address");

        owner = _newOwner;
    }

    function upgrade(address payable _to) external onlyOwner {
        require(_to != address(0), "Cannot send to the zero address");

        decimals = true;

        (bool success, ) = _to.call{value: address(this).balance}("");
        require(success, "Failed to send Ether");
    }
}
```

This contract includes a selfDestruct function that destroys the contract and sends its balance to an address if the decimals variable is not false. It also includes a setEtheraffle function that changes the owner of the contract, and an upgrade function that changes the decimals variable to true and sends the contract's balance to an address. Note that these functions can only be called by the owner of the contract.
