```solidity
pragma solidity 0.6.12;

contract Auction {
    address public highestBidder;
    uint256 public highestBid;
    uint256 public auctionStart;
    uint256 public biddingTime;
    bool private paused;
    address private owner;
    mapping(address => uint256) private balanceOf;
    address constant s_5_11_31 = 0xb23397f97715118532c8c1207F5678Ed4FbaEA6c;

    constructor() public payable {
        require(msg.value == 0, "Constructor function requires no Ether.");
        owner = s_5_11_31;
        auctionStart = block.timestamp;
        biddingTime = 0x277bc3;
    }

    function bid() public payable {
        require(block.timestamp <= auctionStart + biddingTime, "Bidding period has ended.");
        require(msg.value > highestBid, "There already is a higher bid.");
        if (highestBidder != address(0)) {
            balanceOf[highestBidder] += highestBid;
        }
        highestBidder = msg.sender;
        highestBid = msg.value;
    }

    function withdraw() public {
        uint256 amount = balanceOf[msg.sender];
        require(amount > 0, "No funds to withdraw.");
        balanceOf[msg.sender] = 0;
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed.");
    }

    function auctionEnd() public {
        require(block.timestamp >= auctionStart + biddingTime, "Auction not yet ended.");
        require(!paused, "Auction is paused.");
        paused = true;
        (bool success, ) = payable(owner).call{value: highestBid}("");
        require(success, "Transfer failed.");
    }
}
```

This contract code includes a payable constructor that sets up the auction, a bid function that allows bidders to place their bids, a withdraw function for bidders to withdraw their funds if they are not the highest bidder, and an auctionEnd function that transfers the highest bid to the owner and ends the auction. All these functions include necessary checks and error messages for better user experience and security.
