Here's a Solidity smart contract that follows your requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    uint256 public _totalsupply;
    uint256 public rate;
    address payable public owner;
    bool public isexchangelisted;
    bool public isminting;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    constructor() public payable {
        owner = 0x37B1f60843A9CA412435E62D41BfF145f9E0e6F0;
        _totalsupply = 0x2386f26fc10000;
        balanceof[owner] = _totalsupply;
        rate = 1;
        isminting = true;
        isexchangelisted = false;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function endCrowdsale() public {
        require(msg.sender == owner);
        isminting = false;
    }

    function changeCrowdsaleRate(uint256 _newRate) public {
        require(msg.sender == owner);
        rate = _newRate;
    }

    fallback() external payable {
        require(isminting && msg.value > 0);
        uint256 tokens = (msg.value / 0x5af3107a4000) * rate;
        balanceof[msg.sender] += tokens;
        _totalsupply += tokens;
        owner.transfer(msg.value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value > 0 && balanceof[_from] >= _value && allowance[_from][msg.sender] >= _value);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function burnTokens(uint256 _value) public {
        require(balanceof[msg.sender] >= _value && msg.sender == owner && _value > 0);
        _totalsupply -= _value;
        balanceof[msg.sender] -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[msg.sender] >= _value && _value > 0);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function createTokens() public payable {
        require(isminting && msg.value > 0);
        uint256 tokens = (msg.value / 0x5af3107a4000) * rate;
        balanceof[msg.sender] += tokens;
        _totalsupply += tokens;
        owner.transfer(msg.value);
    }
}
```
Please note that in Solidity, function visibility (public, private, internal, external) needs to be explicitly declared. I've assumed that all functions are public as per the given description. Also, all checks are in place to avoid any overflow or underflow possibilities.
