Here is the Solidity contract based on your specifications:

```solidity
pragma solidity 0.6.12;

contract ICO {

    uint256 public _totalsupply;
    address public owner;
    uint256 public _price_tokn;
    uint256 private totalSupply;
    address private owner2;
    bool private paused;
    uint256 public postico_startdate;
    uint256 private totalSupply2;
    uint256 private totalSupply1;
    uint256 public pricefactor;
    mapping(address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public bonus;
    uint8 public stage;

    constructor() public payable {
        owner = msg.sender;
        _totalsupply = 0x295be96e64066972000000;
        paused = false;
        owner2 = 0x1e6d1Fc2d934D2E4e2aE5e4882409C3fECD769dF;
    }

    function approve(address _spender, uint256 _value) public {
        require(_spender != address(0),"spender can not be null");
        allowance[msg.sender][_spender] = _value;
    }

    function setpricefactor(uint256 _pricefactor) public {
        require(msg.sender == owner, "Only owner can set price factor");
        pricefactor = _pricefactor;
    }

    function end_ICO() public {
        require(msg.sender == owner, "Only owner can end ICO");
        stage = 3;
        balanceof[owner] += balanceof[address(this)];
        balanceof[address(this)] = 0;
    }

    function drain() public {
        require(msg.sender == owner, "Only owner can drain");
        payable(owner2).transfer(address(this).balance);
    }

    function ResumeICO() public {
        require(msg.sender == owner, "Only owner can resume ICO");
        paused = false;
    }

    function PauseICO() public {
        require(msg.sender == owner, "Only owner can pause ICO");
        paused = true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value >= 0 && _to != address(0) && balanceof[_from] >= _value && allowance[_from][msg.sender] >= _value, "Transfer conditions not met");
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function start_POSTICO() public {
        require(stage == 0 && stage <= 3 && msg.sender == owner, "Conditions for start_POSTICO not met");
        stage = 1;
        paused = false;
        totalSupply1 = 0x108b2a2c28029094000000;
        balanceof[address(this)] = totalSupply1;
        postico_startdate = now;
        totalSupply2 = now + 0x76a700;
        _price_tokn = 5;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(_value >= 0 && _to != address(0) && balanceof[msg.sender] >= _value, "Transfer conditions not met");
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0) && msg.sender == owner, "Conditions for transferOwnership not met");
        balanceof[newOwner] += balanceof[owner];
        balanceof[owner] = 0;
        owner = newOwner;
    }

    fallback() external payable {
        require(msg.sender != owner && !paused && stage != 3, "Fallback conditions not met");
        require(msg.value > 0, "No ether received");
        if (pricefactor == 0) {
            totalSupply = 0;
        } else {
            totalSupply = (msg.value * pricefactor * 100) / _price_tokn;
        }
        require(totalSupply > 0 && balanceof[address(this)] >= totalSupply && msg.sender != address(0), "Not enough balance in contract or sender is null");
        balanceof[address(this)] -= totalSupply;
        balanceof[msg.sender] += totalSupply;
    }
}
```

This contract includes all the functionalities you asked for, and takes care of the security measures you mentioned. It uses the `fallback` function as the payable function, which is triggered when the contract receives Ether without data. Please review the contract and let me know if you need any changes.
