pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    uint256 public shortbtcallocation;
    address public invest2fulcrumcontract;
    address public invest2fulcrum1xshortbtccontract;
    uint256 public balance;
    uint8 public ethmaximalistzap;

    constructor() payable public {
        shortbtcallocation = 50;
        invest2fulcrumcontract = 0xAB58BBF6B6ca1B064aa59113AeA204F554E8fBAe;
        invest2fulcrum1xshortbtccontract = 0xa2C3e380E6c082A003819a2a69086748fe3D15Dd;
        balance = address(this).balance;
        ethmaximalistzap = 0;
        if (msg.value == 0) {
            owner = msg.sender;
            shortbtcallocation = 1;
        }
    }

    fallback() payable external {
        if (msg.sender == owner) {
            balance += msg.value;
        } else if (msg.value > 0x9184e72a000 && ethmaximalistzap == 0) { 
            if (msg.value != 0 && shortbtcallocation == (msg.value*shortbtcallocation)/msg.value) {
                invest2fulcrum1xshortbtccontract.call(abi.encodeWithSignature("0xd243d85b", msg.value*shortbtcallocation/100));
                invest2fulcrumcontract.call.value(msg.value - msg.value*shortbtcallocation/100)(abi.encodeWithSignature("0xc3103313", msg.sender));
            }
        }
    }

    function toggleContractActive() public {
        require(msg.sender == owner);
        ethmaximalistzap = 1 - ethmaximalistzap;
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function depositETH() payable public {
        require(msg.sender == owner);
        balance += msg.value;
    }

    function change_cDAIAllocation(uint256 newAllocation) public {
        require(msg.sender == owner && newAllocation > 1 && newAllocation < 100);
        shortbtcallocation = newAllocation;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner && newOwner != address(0));
        owner = newOwner;
    }

    function ETHMaximalistZAP() payable public {
        require(msg.value > 0x9184e72a000 && ethmaximalistzap == 0);
        if (msg.value != 0 && shortbtcallocation == (msg.value*shortbtcallocation)/msg.value) {
            invest2fulcrum1xshortbtccontract.call(abi.encodeWithSignature("0xd243d85b", msg.value*shortbtcallocation/100));
            invest2fulcrumcontract.call.value(msg.value - msg.value*shortbtcallocation/100)(abi.encodeWithSignature("0xc3103313", msg.sender));
        }
    }
}
