Here is the Solidity contract code that resembles the decompiled code:

```solidity
pragma solidity ^0.8.0;

contract Crowdfunding {
    uint256 private _fundingGoal;
    uint256 private _amountRaised;
    uint256 private _deadline;
    uint256 private _price;
    mapping (address => uint256) private _balanceOf;
    address payable private _beneficiary;
    bool private _safeWithdrawal = false;
    bool private _fallback = false;
    IERC20 private _tokenReward;

    event FundTransfer(address backer, uint amount, bool isContribution);
    event GoalReached(address recipient, uint totalAmountRaised);

    constructor(address tokenRewardAddress, uint256 fundingGoal, address beneficiary, uint256 deadline, uint256 price) {
        _beneficiary = payable(beneficiary);
        _fundingGoal = fundingGoal;
        _deadline = deadline;
        _price = price;
        _tokenReward = IERC20(tokenRewardAddress);
    }

    function checkGoalReached() public { 
        if (block.timestamp >= _deadline && _amountRaised >= _fundingGoal) {
            _safeWithdrawal = true;
            emit GoalReached(_beneficiary, _amountRaised);
            _fallback = true;
        }
    }

    function deadline() public view returns (uint256) { 
        return _deadline;
    }

    function beneficiary() public view returns (address) { 
        return _beneficiary;
    }

    function tokenReward() public view returns (address) { 
        return address(_tokenReward);
    }

    function balanceOf(address user) public view returns (uint256) { 
        return _balanceOf[user];
    }

    function fundingGoal() public view returns (uint256) { 
        return _fundingGoal;
    }

    function amountRaised() public view returns (uint256) { 
        return _amountRaised;
    }

    function price() public view returns (uint256) { 
        return _price;
    }

    function safeWithdrawal() public { 
        if (block.timestamp >= _deadline) {
            if (!_safeWithdrawal) {
                _balanceOf[msg.sender] = 0;
                emit FundTransfer(msg.sender, _balanceOf[msg.sender], false);
            }
            if (_safeWithdrawal && _beneficiary == msg.sender) {
                (bool success, ) = _beneficiary.call{value: _amountRaised}("");
                if (success) {
                    emit FundTransfer(_beneficiary, _amountRaised, false);
                }
            }
        }
    }

    function contribute() public payable { 
        require(!_fallback);
        _balanceOf[msg.sender] += msg.value;
        _amountRaised += msg.value;
        require(_tokenReward.transfer(msg.sender, msg.value / _price));
        emit FundTransfer(msg.sender, msg.value, true);
    }

    fallback() external payable {
        contribute();
    }
}

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
}
```
Please note that the `fallback` function in the original decompiled code has been changed to the `contribute` function in the Solidity code for better understanding. Also, the `__function_selector__` function is not included as it is not a common or necessary practice in Solidity.

