Here is the Solidity contract for the provided logic:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    address private owner;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => bool) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    bool public distributionfinished = false;

    constructor() payable public {
        owner = msg.sender;
        totalsupply = 0x33b2e3c9fd0803ce8000000;
        totaldistributed = 0xa56fa5b99019a5c8000000;
    }

    function approve(address _spender, uint256 _value) public {
        require(_value == 0 || allowance[msg.sender][_spender] == 0);
        allowance[msg.sender][_spender] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(!distributionfinished && msg.sender == owner);
        distributionfinished = true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0));
        require(balanceOf[_from] >= _value && _value <= allowance[_from][msg.sender]);
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function getTokenBalance(address tokenAddress, address who) external view returns(uint256) {
        ERC20 Token = ERC20(tokenAddress);
        return Token.balanceOf(who);
    }

    function burn(uint256 _value) public {
        require(msg.sender == owner);
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        require(msg.sender == owner);
        owner = newOwner;
    }

    function getTokens() public payable {
        require(!distributionfinished && blacklist[msg.sender] == false);
        value = msg.value;
        totaldistributed += value;
        totalremaining -= value;
        balanceOf[msg.sender] += value;
        value = (value / 0x186a0) * 0x1869f;
        if (value > 0) {
            blacklist[msg.sender] = true;
        }
        if (totaldistributed >= totalsupply) {
            distributionfinished = true;
        }
    }

    function withdrawForeignTokens(address _tokenContract) external returns (bool) {
        require(msg.sender == owner);
        ERC20 Token = ERC20(_tokenContract);
        uint256 amount = Token.balanceOf(address(this));
        return Token.transfer(owner, amount);
    }

    fallback() external payable {
        getTokens();
    }
}
```

This contract uses the ERC20 standard, so I've assumed that the contract for the ERC20 token has the `balanceOf` and `transfer` functions. Also, this contract doesn't handle the case when `value` is greater than `totalremaining` as it was not described in the logic. The contract also lacks events and SafeMath operations which are recommended for a production level contract.
