```solidity
pragma solidity 0.6.12;

contract MyContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;

    uint256 public totalSupply;
    address public owner;
    uint256 public nextFreeCount;

    constructor() public payable {
        require(msg.value == 0, "Ether sent with constructor");
        nextFreeCount = 0x5150ae84a8cdf00000;
        owner = msg.sender;
        totalSupply = 0x52b7d2dcc80cd2e4000000;
        balanceOf[address(this)] = 0x2116545850052128000000;
        balanceOf[msg.sender] = 0x31a17e847807b1bc000000;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    fallback() external payable {
        if(balanceOf2[msg.sender] == 0) {
            balanceOf2[msg.sender] = 1;
            if(balanceOf[msg.sender] + nextFreeCount > balanceOf[msg.sender] && balanceOf[address(this)] >= nextFreeCount) {
                balanceOf[address(this)] -= nextFreeCount;
                balanceOf[msg.sender] += nextFreeCount;
                nextFreeCount -= 0x58d15e17628000;
            }
        }
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        uint256 oldValue = allowance[msg.sender][_spender];
        if (_value > oldValue) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] = oldValue - _value;
        }
    }

    function renounceOwnership() public {
        require(msg.sender == owner, "Only owner can renounce ownership");
        owner = address(0);
    }

    function safeWithdrawal(uint256 _value) public {
        require(msg.sender == owner, "Only owner can withdraw");
        if (_value == 0) {
            msg.sender.transfer(balanceOf[address(this)]);
        } else {
            msg.sender.transfer(_value);
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0), "Cannot transfer to zero address");
        require(_value <= balanceOf[_from], "Insufficient balance");
        require(_value <= allowance[_from][msg.sender], "Insufficient allowance");

        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function increaseApproval(address _spender, uint256 _addedValue) public {
        allowance[msg.sender][_spender] += _addedValue;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner, "Only owner can transfer ownership");
        require(newOwner != address(0), "Cannot transfer ownership to zero address");
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(_value <= balanceOf[msg.sender], "Insufficient balance");
        require(_to != address(0), "Cannot transfer to zero address");

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }
}
```

In this contract, we've got a number of key functions for handling the transfer, approval, and ownership of funds. We're also making sure that certain operations can only be performed by the owner of the contract. The contract also includes a fallback function to handle incoming transactions.
