pragma solidity 0.6.12;

contract MyContract {
    mapping(address => uint256) public earnings;
    mapping(address => uint256) public dividendshares;
    uint256 public totaldividendshares;
    uint256 public dividendfund;
    uint256 public round;
    uint256 public pot;
    address payable public leader;
    uint256 public leadertimestamp;
    uint256 public leaderbid;

    constructor() public payable {
        require(msg.value > 0);
        leader = msg.sender;
        totaldividendshares = 0;
        dividendfund = 0;
        round = 0;
        pot = msg.value;
        leadertimestamp = block.timestamp;
        leaderbid = 0;
    }

    function withdrawEarnings() public {
        require(earnings[msg.sender] > 0);
        require(earnings[msg.sender] <= address(this).balance);
        msg.sender.transfer(earnings[msg.sender]);
        earnings[msg.sender] = 0;
    }

    function withdrawDividends() public {
        require(dividendshares[msg.sender] > 0);
        if(address(this).balance > 0 && dividendfund == 0) {
            totaldividendshares -= dividendshares[msg.sender];
            dividendshares[msg.sender] = 0;
            dividendfund -= 0;
            msg.sender.transfer(0);
        }
        else if((dividendfund*dividendshares[msg.sender])/totaldividendshares <= address(this).balance && dividendfund != 0) {
            uint256 amount = (dividendfund*dividendshares[msg.sender])/totaldividendshares;
            msg.sender.transfer(amount);
            dividendfund -= amount;
            totaldividendshares -= dividendshares[msg.sender];
            dividendshares[msg.sender] = 0;
        }
    }

    function bid() public payable {
        require(msg.value != 0);
        if(msg.value < 0) {
            if(pot == 0 || (pot-((pot*1)/10)) == 0 || (((block.timestamp-leadertimestamp)/300)*leaderbid) == 0) {
                dividendfund += (msg.value*1)/2;
                pot += msg.value - ((msg.value*1)/2);
            }
        }
        else if(pot != 0) {
            if(msg.value < (pot*1)/1000 || msg.value < (((pot-((pot*1)/10))*1)/1000)) {
                dividendfund += (msg.value*1)/2;
                pot += msg.value - ((msg.value*1)/2);
            }
        }
        else if((((block.timestamp-leadertimestamp)/300)*leaderbid) != 0 && (pot-(((((block.timestamp-leadertimestamp)/300)*leaderbid)*10)/100)) != 0 && msg.value < (((pot-(((((block.timestamp-leadertimestamp)/300)*leaderbid)*10)/100))*1)/1000)) {
            dividendfund += (msg.value*1)/2;
            pot += msg.value - ((msg.value*1)/2);
        }
        else if(msg.value == 0) {
            if(pot != 0) {
                if(msg.value < (pot*1)/1000 || msg.value < (((pot-((pot*1)/10))*1)/1000)) {
                    dividendfund = dividendfund;
                    pot += msg.value;
                }
            }
            else if((((block.timestamp-leadertimestamp)/300)*leaderbid) != 0 && (pot-(((((block.timestamp-leadertimestamp)/300)*leaderbid)*10)/100)) != 0 && msg.value < (((pot-(((((block.timestamp-leadertimestamp)/300)*leaderbid)*10)/100))*1)/1000)) {
                dividendfund = dividendfund;
                pot += msg.value;
            }
        }
    }
}
