Here is the contract code based on the given descriptions:

```solidity
pragma solidity 0.6.12;

contract ActivityContract {
    bool public paused;
    address public owner;
    address public nonfungiblecontract;
    bool public isactivitycore;
    address public storagecontract;
    address private token;

    constructor() payable public {
        paused = false;
        isactivitycore = true;
    }

    function getActivity(uint16 arg) external {
        token.call(abi.encodePacked("activities", arg));
    }

    function reclaimContract(address arg) external {
        require(owner == msg.sender && paused);
        arg.call(abi.encodePacked("transferOwnership", owner));
    }

    function withdrawBalance() external {
        if(msg.sender == owner) {
            payable(nonfungiblecontract).transfer(address(this).balance);
        } else {
            require(msg.sender != nonfungiblecontract);
            payable(nonfungiblecontract).transfer(address(this).balance);
        }
    }

    function pause() external {
        require(owner == msg.sender && !paused);
        paused = true;
    }

    function destroy() external {
        require(owner == msg.sender && paused);
        (bool success, bytes memory data) = storagecontract.call(abi.encodePacked("owner"));
        require(success);
        address v0 = abi.decode(data, (address));
        require(v0 != address(this));
        selfdestruct(payable(owner));
    }

    function setNFTAddress(address arg) external {
        require(arg != address(0) && msg.sender == owner);
        (bool success, bytes memory data) = arg.call(abi.encodePacked("supportsInterface", bytes32(0x9f40b77900000000000000000000000000000000000000000000000000000000)));
        require(success);
        bool v0 = abi.decode(data, (bool));
        require(v0);
        nonfungiblecontract = arg;
    }

    function unpause() external {
        require(owner == msg.sender && nonfungiblecontract != address(0) && paused && storagecontract != address(0));
        (bool success, bytes memory data) = storagecontract.call(abi.encodePacked("owner"));
        require(success);
        address v0 = abi.decode(data, (address));
        require(v0 == address(this));
        paused = false;
    }

    function deleteActivity(uint16 arg) external {
        require(owner == msg.sender && paused);
        token.call(abi.encodePacked("deleteActivity", arg));
    }

    function createActivity(uint16 arg1, uint16 arg2, uint128 arg3, uint64 arg4, uint64 arg5) external {
        require(owner == msg.sender && !paused);
        token.call(abi.encodePacked("createActivity", arg1, arg2, arg3, arg4, arg5));
    }

    function bid(uint16 arg1, uint16 arg2) external payable {
        require(!paused);
        (bool success, bytes memory data) = token.call(abi.encodePacked("activities", arg1));
        require(success);
        uint v0 = abi.decode(data, (uint));
        require(msg.value >= v0 && v0 > 0);
        (success, data) = token.call(abi.encodePacked("getBuyerAddress", arg1, arg2));
        require(success);
        address v1 = abi.decode(data, (address));
        require(v1 == address(0));
        (success, data) = token.call(abi.encodePacked("getAddressBoughtCount", arg1, msg.sender));
        require(success);
        uint v2 = abi.decode(data, (uint));
        require(v2 < v0);
        token.call(abi.encodePacked("sellPackToAddress", arg1, arg2, msg.sender));
    }

    function transferOwnership(address arg) external {
        require(arg != address(0) && msg.sender == owner);
        owner = arg;
    }

    function withdrawBalanceFromStorageContract() external {
        if(msg.sender == owner) {
            storagecontract.call(abi.encodePacked("withdrawBalance"));
        } else {
            require(msg.sender != nonfungiblecontract);
            storagecontract.call(abi.encodePacked("withdrawBalance"));
        }
    }
}
```

Please note that this contract is not complete and cannot be deployed as is. This is because it lacks the implementation of the `storagecontract` and `token` contract. You need to link these contracts to use their methods.
