```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceOf;
    mapping (address => uint256) public orders;
    mapping (address => uint256) public orderFills;

    constructor() public {
        admin = msg.sender;
    }

    function order(address _address, uint256 _amount, address _address2, uint256 _uint1, uint256 _uint2, uint256 _uint3) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _amount, _address2, _uint1, _uint2, _uint3));
        orders[msg.sender] = 1;
    }

    function cancelOrder(address _address, uint256 _amount, address _address2, uint256 _uint1, uint256 _uint2, uint256 _uint3, uint8 _uint8, bytes32 _b1, bytes32 _b2) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _amount, _address2, _uint1, _uint2, _uint3));
        if (orders[msg.sender] != 0) {
            orderFills[msg.sender] = _amount;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), _uint8, _b1, _b2);
            orderFills[msg.sender] = _amount;
        }
    }

    function changeFeeMake(uint256 _feemake) public {
        require(_feemake <= feemake, "Fee is too high");
        require(msg.sender == admin, "Only admin can change the fee");
        feemake = _feemake;
    }

    function withdraw(uint256 _amount) public {
        require(balanceOf[address(0)][msg.sender] >= _amount, "Insufficient balance");
        balanceOf[address(0)][msg.sender] -= _amount;
        msg.sender.transfer(_amount);
    }

    function changeFeeAccount(address _feeaccount) public {
        require(msg.sender == admin, "Only admin can change the fee account");
        feeaccount = _feeaccount;
    }

    function changeAdmin(address _newAdmin) public {
        require(msg.sender == admin, "Only admin can change the admin account");
        admin = _newAdmin;
    }

    function depositToken(address _token, uint256 _amount) public {
        require(_token != address(0), "Token address cannot be 0");
        (bool success,) = _token.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", msg.sender, address(this), _amount));
        require(success, "Transfer failed");
        balanceOf[_token][msg.sender] += _amount;
    }

    function changeFeeTake(uint256 _feetake) public {
        require(_feetake <= feetake, "Fee is too high");
        require(msg.sender == admin, "Only admin can change the fee");
        feetake = _feetake;
    }

    function changeAccountLevelsAddr(address _accountlevelsaddr) public {
        require(msg.sender == admin, "Only admin can change the account levels address");
        accountlevelsaddr = _accountlevelsaddr;
    }

    function deposit() public payable {
        balanceOf[address(0)][msg.sender] += msg.value;
    }

    function withdrawToken(address _token, uint256 _amount) public {
        require(_token != address(0), "Token address cannot be 0");
        require(balanceOf[_token][msg.sender] >= _amount, "Insufficient token balance");
        balanceOf[_token][msg.sender] -= _amount;
        (bool success,) = _token.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, _amount));
        require(success, "Transfer failed");
    }
}
