    mapping(address => uint256) private s_4;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private s_6;

    function approve(address _to, uint256 _value) public payable {
        require(_to != address(0) && msg.sender != address(0));
        allowance[msg.sender][_to] = _value;
    }

    function decreaseAllowance(address _to, uint256 _value) public payable {
        require(_value <= allowance[msg.sender][_to] && _to != address(0) && msg.sender != address(0));
        allowance[msg.sender][_to] -= _value;
    }

    function increaseAllowance(address _to, uint256 _value) public payable {
        require(msg.sender != address(0) && _to != address(0));
        require(_value + allowance[msg.sender][_to] >= allowance[msg.sender][_to]);
        allowance[msg.sender][_to] += _value;
    }

    function transfer(address _to, uint256 _value) public payable {
        require(_to != address(0) && msg.sender != address(0));
        (bool success, bytes memory data) = address(0x5491305204DaCa75317A0bC8C332dD61Dbb77Bc8).staticcall(abi.encodeWithSignature("balanceOf(address)", msg.sender));
        require(success);
        uint256 v0 = abi.decode(data, (uint256));

        if (_value <= s_4[msg.sender]) {
            if (s_6[msg.sender] == 0) {
                require(s_4[msg.sender] >= _value);
                s_4[msg.sender] -= _value;
                require(_value + s_4[_to] >= s_4[_to]);
                s_4[_to] += _value;
            }
            if (v0 <= 0 && s_6[msg.sender] != 0) {
                require(s_4[msg.sender] >= _value);
                s_4[msg.sender] -= _value;
                require(_value + s_4[_to] >= s_4[_to]);
                s_4[_to] += _value;
            }
        }
        if (_value <= s_6[msg.sender]) {
            require(s_6[msg.sender] >= _value);
            s_6[msg.sender] -= _value;
            require(_value + s_4[_to] >= s_4[_to]);
            s_4[_to] += _value;
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public payable {
        require(_to != address(0));
        (bool success, bytes memory data) = address(0x5491305204DaCa75317A0bC8C332dD61Dbb77Bc8).staticcall(abi.encodeWithSignature("balanceOf(address)", _from));
        require(success);
        uint256 v0 = abi.decode(data, (uint256));

        if (_from != address(0)) {
            if (_value <= s_4[_from]) {
                if (s_6[_from] == 0) {
                    require(s_4[_from] >= _value);
                    s_4[_from] -= _value;
                    require(_value + s_4[_to] >= s_4[_to]);
                    s_4[_to] += _value;
                    require(_value <= allowance[_from][msg.sender]);
                    allowance[_from][msg.sender] -= _value;
                }
                if (v0 <= 0 && s_6[_from] != 0) {
                    require(s_4[_from] >= _value);
                    s_4[_from] -= _value;
                    require(_value + s_4[_to] >= s_4[_to]);
                    s_4[_to] += _value;
                    require(_value <= allowance[_from][msg.sender]);
                    allowance[_from][msg.sender] -= _value;
                }
            }
            if (_value <= s_6[_from]) {
                require(s_6[_from] >= _value);
                s_6[_from] -= _value;
                require(_value + s_4[_to] >= s_4[_to]);
                s_4[_to] += _value;
                require(_value <= allowance[_from][msg.sender]);
                allowance[_from][msg.sender] -= _value;
            }
        }
    }
}
