Here is the contract code based on your requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    string public standard;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    address public owner;
    address public newowner;

    constructor() public {
        owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public {
        require(_spender != address(0), "Invalid address");
        allowance[msg.sender][_spender] = _value;
    }

    fallback() external payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function acceptOwnership() public {
        require(msg.sender == newowner, "Only new owner can accept ownership");
        owner = newowner;
        newowner = address(0);
    }

    function withdraw(uint256 amount) public {
        require(balanceof[msg.sender] >= amount, "Insufficient balance");
        balanceof[msg.sender] -= amount;
        totalsupply -= amount;
        msg.sender.transfer(amount);
    }

    function withdrawTokens(address _token, address _to, uint256 _value) public {
        require(_token != address(0) && _to != address(this), "Invalid address");
        require(owner == msg.sender, "Only owner can withdraw tokens");
        require(_to != address(0), "Invalid address");
        (bool success,) = _token.call(abi.encodeWithSelector(0xa9059cbb, _to, _value));
        require(success, "Transfer failed");
    }

    function transferOwnership(address _newowner) public {
        require(msg.sender == owner && _newowner != owner, "Only owner can transfer ownership");
        newowner = _newowner;
    }

    function deposit() public payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_from != address(0) && _to != address(0) && _to != address(this), "Invalid address");
        require(allowance[_from][msg.sender] >= _value, "Insufficient allowance");
        require(balanceof[_from] >= _value, "Insufficient balance");
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _to != address(this), "Invalid address");
        require(balanceof[msg.sender] >= _value, "Insufficient balance");
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }
}
```
