// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

contract AlienContract {
    uint256 public eggs_to_hatch_1alien;
    uint256 public starting_alien;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    address payable public ceoaddress;
    bool public initialized;
    mapping(address => uint256) public hatcheryalien;
    mapping(address => uint256) public claimedeggs;
    mapping(address => uint256) public lasthatch;
    mapping(address => address) public referrals;
    uint256 public marketeggs;

    constructor() public payable {
        if (msg.value == 0) {
            ceoaddress = 0x4B4f724B936290bDADC87439856Eaf2671eb5072;
        }
        eggs_to_hatch_1alien = 0x15180;
        starting_alien = 100;
        totalSupply = 10000;
        totalSupply1 = 5000;
        initialized = false;
    }

    function seedMarket(uint256 _marketeggs) public payable {
        if (marketeggs == 0){
            initialized = true;
            marketeggs = _marketeggs;
        }
    }

    function setFreeAlien(uint16 _starting_alien) public {
        require(_starting_alien >= 10);
        if (msg.sender == ceoaddress) {
            starting_alien = _starting_alien;
        }
    }

    function getFreeAlien() public {
        require(initialized);
        if (hatcheryalien[msg.sender] == 0) {
            lasthatch[msg.sender] = now;
            hatcheryalien[msg.sender] = starting_alien;
        }
    }

    function hatchEggs(address _referral) public {
        require(initialized);
        if (now - lasthatch[msg.sender] > 0) {
            if (hatcheryalien[msg.sender] > 0) {
                claimedeggs[_referral] += hatcheryalien[msg.sender];
                claimedeggs[msg.sender] = 0;
                lasthatch[msg.sender] = now;
            }
            if (referrals[msg.sender] == address(0)) {
                referrals[msg.sender] = _referral;
            }
        }
    }

    function sellEggs() public {
        require(initialized);
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = now;
        if (eggs_to_hatch_1alien == 0) {
            marketeggs += claimedeggs[msg.sender];
            if (totalSupply == 0) {
                if (totalSupply1 == 0) {
                    uint256 value = (4 * 0) / 100;
                    ceoaddress.transfer(value);
                    msg.sender.transfer(0 - value);
                }
            }
        }
    }

    function buyEggs() public payable {
        require(msg.value > 0);
        require(initialized);
        uint256 value = (4 * msg.value) / 100;
        ceoaddress.transfer(value);
        if (totalSupply == 0) {
            if (totalSupply1 == 0 && 0 != 0) {
                claimedeggs[msg.sender] += (0 - value);
            } else if (totalSupply1 != 0 && 0 == 0) {
                claimedeggs[msg.sender] += 0;
            }
        }
    }
}
