Here's the contract code based on your requirements:

```solidity
pragma solidity 0.6.12;

contract RealitioContract {
    address public owner;
    address public realitio;
    mapping(bytes32 => uint256) public arbitration_bounties;
    uint256 public disputefee;
    mapping(bytes32 => uint256) public disputefees;
    string public metadata;

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Not owner");
        _;
    }

    function setDisputeFee(uint256 _disputefee) public onlyOwner {
        disputefee = _disputefee;
    }

    function setCustomDisputeFee(bytes32 _questionID, uint256 _disputefee) public onlyOwner {
        disputefees[_questionID] = _disputefee;
    }

    function setRealitio(address _realitio) public onlyOwner {
        realitio = _realitio;
    }

    function withdraw(address payable _to) public onlyOwner {
        require(address(this).balance > 0, "Nothing to withdraw");
        _to.transfer(address(this).balance);
    }

    function setQuestionFee(uint256 _fee) public onlyOwner {
        (bool success,) = realitio.call(abi.encodeWithSignature("setQuestionFee(uint256)", _fee));
        require(success, "Failed to call setQuestionFee");
    }

    function transferOwnership(address _newOwner) public onlyOwner {
        owner = _newOwner;
    }

    function callWithdraw() public onlyOwner {
        (bool success,) = realitio.call(abi.encodeWithSignature("withdraw()"));
        require(success, "Failed to call withdraw");
    }

    function submitAnswerByArbitrator(bytes32 _questionID, bytes32 _answer, address _arbitrator) public onlyOwner {
        arbitration_bounties[_questionID] = 0;
        (bool success,) = realitio.call(abi.encodeWithSignature("submitAnswerByArbitrator(bytes32,bytes32,address)", _questionID, _answer, _arbitrator));
        require(success, "Failed to submit answer");
    }

    function requestArbitration(bytes32 _questionID, uint256 _maxPrevious) public payable {
        arbitration_bounties[_questionID] = msg.value + arbitration_bounties[_questionID];
        uint256 _disputefee = (disputefees[_questionID] > 0) ? disputefees[_questionID] : disputefee;
        if (arbitration_bounties[_questionID] >= _disputefee) {
            (bool success,) = realitio.call(abi.encodeWithSignature("notifyOfArbitrationRequest(bytes32,address,uint256)", _questionID, msg.sender, _maxPrevious));
            require(success, "Failed to request arbitration");
        } else {
            (bool success,) = realitio.call(abi.encodeWithSignature("isFinalized(bytes32)", _questionID));
            require(success, "Question not finalized");
        }
    }

    function setMetaData(string memory _metadata) public onlyOwner {
        metadata = _metadata;
    }
}
```
This contract defines a RealitioContract with the specified functions. Each function checks if the caller is the owner of the contract before executing. The contract has a payable function `requestArbitration`, which accepts ether and updates the arbitration bounties accordingly and makes the necessary external calls. It also checks if the question is finalized before requesting arbitration.
