```solidity
pragma solidity 0.6.12;

contract TokenSale {
    address public owner;
    string public name;
    string public symbol;
    uint256 public decimals;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    address public multisigwallet;
    uint256 public amountraised;
    uint256 public starttime;
    uint256 public stoptime;
    uint256 public fixedtotalsupply;
    uint256 public price;
    uint256 public minimuminvestment;
    uint256 public crowdsaletarget;
    bool private paused;
    uint256 private rate;

    modifier onlyOwner {
        require(msg.sender == owner, "Only owner can call this function.");
        _;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function setMinimumInvestment(uint256 _value) public onlyOwner {
        minimuminvestment = _value;
    }

    function setPrice(uint256 _value) public onlyOwner {
        require(_value > 0, "Price should be greater than 0.");
        price = _value;
    }

    function startSale(uint256 _start, uint256 _stop, uint256 _price, address _wallet, uint256 _minInvestment, uint256 _target) public onlyOwner {
        require(_stop > block.timestamp, "Stop time should be in future.");
        starttime = _start;
        stoptime = _stop;
        amountraised = 0;
        paused = false;
        if(_price > 0) {
            price = _price;
            multisigwallet = _wallet;
            minimuminvestment = _minInvestment;
            crowdsaletarget = _target;
        }
    }

    function stopSale() public onlyOwner {
        stoptime = block.timestamp;
        paused = true;
    }

    function setMultiSigWallet(address _wallet) public onlyOwner {
        multisigwallet = _wallet;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        owner = newOwner;
    }

    function setCrowdsaleTarget(uint256 _target) public onlyOwner {
        crowdsaletarget = _target;
    }

    function mintToken(address _to, uint256 _value) public onlyOwner {
        require(_value > 0 && _value + totalsupply <= fixedtotalsupply, "Invalid minting amount.");
        balanceof[_to] += _value;
        totalsupply += _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceof[_from] && _to != address(0) && _value <= allowance[_from][msg.sender] && _value + balanceof[_to] > balanceof[_to], "Transfer not valid.");
        balanceof[_to] += _value;
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_value + balanceof[_to] > balanceof[_to] && _to != address(0) && _value <= balanceof[msg.sender], "Transfer not valid.");
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    receive() external payable {
        require(msg.value >= minimuminvestment && block.timestamp < stoptime && totalsupply < fixedtotalsupply && paused == false && (msg.value / 0xde0b6b3a7640000 + amountraised) <= crowdsaletarget, "Investment not valid.");
        amountraised += msg.value / 0xde0b6b3a7640000;
        totalsupply += msg.value / 0xde0b6b3a7640000;
    }
}
```
This contract follows the description and specifications provided, and it includes functions for approving token transfers, setting minimum investment amount, starting and stopping a token sale, setting the multisig wallet address, transferring ownership of the contract, setting the target amount for the crowdsale, minting tokens, transferring tokens from one address to another, and a payable fallback function for receiving funds.
