pragma solidity 0.6.12;

contract YourContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;
    mapping(address => uint256) public etherBalanceOf;
    address public master;
    bool public transfer;
    uint256 public totalSupply;
    uint256 public totalMigrated;
    uint256 public amountOfTotalParticipants;
    uint256 public timeLeftBeforeCrowdsale = 0x5a1a036a;
    uint256 public timeLeft = 0x5a1a291d;
    address public migrationAgent;

    constructor() public payable {
        transfer = true;
        if (msg.value == 0) {
            master = msg.sender;
            timeLeftBeforeCrowdsale = 0x5a1a036a;
            timeLeft = 0x5a1a291d;
        }
    }

    function approve(address _spender, uint256 _value) public {
        if (!transfer) {
            allowance[msg.sender][_spender] = _value;
        }
    }

    function() external payable {
        require(block.timestamp > timeLeftBeforeCrowdsale && block.timestamp < timeLeft);
        require(msg.value > 0);
        require(msg.value * 0x7530 <= 0x27b46536c66c8e3000000 - totalSupply);
        require(transfer);
        totalSupply += msg.value * 0x7530;
        balanceOf[msg.sender] += msg.value * 0x7530;
        etherBalanceOf[msg.sender] += msg.value;
        amountOfTotalParticipants++;
    }

    function finalize() public {
        require(transfer);
        transfer = false;
        balanceOf[master] += totalSupply * 25 / 62;
        totalSupply += totalSupply * 25 / 62;
        (bool success, ) = master.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    function migrate(uint256 _value) external {
        require(!transfer);
        require(_value <= balanceOf[msg.sender]);
        require(_value > 0);
        require(migrationAgent != address(0));
        balanceOf[msg.sender] -= _value;
        totalSupply -= _value;
        totalMigrated += _value;
        // Assuming the migration agent contract has migrateFrom method
        (bool success, ) = migrationAgent.call(abi.encodeWithSignature("migrateFrom(address,uint256)", msg.sender, _value));
        require(success, "Migration failed.");
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(!transfer);
        require(balanceOf[_to] + _value > balanceOf[_to]);
        require(balanceOf[_from] >= _value);
        require(allowance[_from][msg.sender] >= _value);
        require(_value > 0);
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function refund() public {
        require(balanceOf[msg.sender] > 0);
        require(totalSupply < 0xde0b6b3a7640000);
        require(transfer);
        require(block.timestamp > timeLeft);
        totalSupply -= balanceOf[msg.sender];
        balanceOf[msg.sender] = 0;
        (bool success, ) = msg.sender.call{value: etherBalanceOf[msg.sender]}("");
        require(success, "Refund failed.");
        etherBalanceOf[msg.sender] = 0;
    }

    function setMigrationAgent(address _newAgent) public {
        require(!transfer);
        require(msg.sender == master);
        require(migrationAgent == address(0));
        migrationAgent = _newAgent;
    }

    function transfer(address _to, uint256 _value) public {
        require(!transfer);
        require(_value > 0);
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        balanceOf2[msg.sender] = block.timestamp;
    }
}
