Here is the contract code according to your specification:

```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public admin1;
    address public admin2;
    address public admin3;
    uint256 public duration;
    uint256 public hardcap;
    uint256 public raised;
    uint256 public bonus;
    address public benefit;
    uint256 public start;
    address private token;
    address public tokenaddress;
    uint256 public tokensperether;
    mapping(address => uint256) public balanceof;

    function pay() external payable {
        require(start + duration > block.timestamp, "Sale period has ended");
        require(start > 0, "Sale has not started");
        require(raised < hardcap, "Hardcap is reached");
        require(start <= block.timestamp, "Sale has not started");

        uint256 amountTokens = (msg.value * tokensperether) + ((msg.value * tokensperether * bonus) / 100);

        (bool success, ) = tokenaddress.call(abi.encodeWithSignature("transferPresale(address,uint256)", msg.sender, amountTokens));
        require(success, "Failed to transfer tokens");

        raised += msg.value;
        balanceof[msg.sender] += msg.value;
    }

    function getFunds(uint256 amount) external {
        require(amount <= address(this).balance, "Insufficient contract balance");
        require(benefit != address(0), "Benefit address not set");

        if (msg.sender == admin1 || msg.sender == admin2 || msg.sender == admin3) {
            (bool success, ) = payable(benefit).call.value(amount)("");
            require(success, "Failed to transfer funds");
        }
    }

    function setBenefit(address _benefit) external {
        require(msg.sender == admin1 || msg.sender == admin2 || msg.sender == admin3, "Only admins can set benefit");
        benefit = _benefit;
    }

    function setAdmin(address _admin) external {
        require(_admin != admin1 && _admin != admin2 && _admin != admin3, "Admin already exists");

        if (msg.sender == admin1) admin1 = _admin;
        else if (msg.sender == admin2) admin2 = _admin;
        else if (msg.sender == admin3) admin3 = _admin;
    }
}
```

Please note that this code is not fully tested and might have some security issues. For production, it is recommended to have a more thorough security audit.
