Here is the Solidity code according to the given specifications:

```solidity
pragma solidity 0.6.12;

contract MyContract {

    address public owner;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public creationblock;
    uint256 public tokenslicecap;
    uint256 public ethslicecap;
    mapping (address => uint256) public blocklockof;

    constructor() public {
        owner = msg.sender;
        totalsupply = 0xde0b6b3a7640000;
        balanceof[msg.sender] = totalsupply;
        creationblock = block.number;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        require(_spender != address(0));

        if(_value == 0) {
            allowance[msg.sender][_spender] = _value;
        } else if (allowance[msg.sender][_spender] == 0) {
            allowance[msg.sender][_spender] = _value;
        }

        return true;
    }

    function withdrawToken(address _tokenContract) public returns (bool success) {
        require(msg.sender == owner);
        require(_tokenContract != address(this));

        ERC20 token = ERC20(_tokenContract);
        uint256 v0 = token.balanceOf(address(this));
        token.transfer(owner, v0);

        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_from != address(0));
        require(_to != address(0));
        require(_to != address(this));
        require(block.number >= blocklockof[_from]);

        balanceof[_to] += _value;
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;

        return true;
    }

    function donateETHToMainPot() public payable {
        require(msg.value > 0);
        ethslicecap += msg.value;
    }

    function donateTokensToMainPot(uint256 _value) public {
        balanceof[msg.sender] -= _value;
        balanceof[address(this)] += _value;
        tokenslicecap += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(block.number >= blocklockof[msg.sender]);
        require(_to != address(this));

        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;

        blocklockof[msg.sender] = block.number + ((block.number - creationblock) / 5);
    }

    function withdrawETH() public {
        require(msg.sender == owner);
        uint256 amount = address(this).balance - ethslicecap;
        msg.sender.transfer(amount);
    }

    function transfer(address _to, uint256 _value, bytes memory _data) public {
        require(block.number >= blocklockof[msg.sender]);
        require(_to != address(this));

        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;

        ERC223 _erc223Contract = ERC223(_to);
        _erc223Contract.tokenFallback(msg.sender, _value, _data);
    }

    receive() external payable {
        require(msg.value > 0);
        require(tokenslicecap >= 0x38d7ea4c68000);

        uint256 _value = (msg.value * tokenslicecap) / ethslicecap;
        require(_value <= (30 * tokenslicecap) / 100);

        balanceof[address(this)] -= _value;
        balanceof[msg.sender] += _value;
        tokenslicecap -= _value;

        ethslicecap += msg.value;
    }

    function sellTokensToPot(uint256 _value) public {
        require(ethslicecap > 0);

        uint256 amount = (_value * ethslicecap) / tokenslicecap;
        require(amount <= (30 * ethslicecap) / 100);

        balanceof[msg.sender] -= _value;
        balanceof[address(this)] += _value;
        tokenslicecap += _value;

        ethslicecap -= amount;
        msg.sender.transfer(amount);
    }
}

interface ERC20 {
    function balanceOf(address _owner) external view returns (uint256 balance);
    function transfer(address _to, uint256 _value) external returns (bool success);
}

interface ERC223 {
    function tokenFallback(address _from, uint256 _value, bytes calldata _data) external;
}

```
Please note that you need to have ERC20 and ERC223 token contracts for this to work. You may replace `ERC20` and `ERC223` with the actual contract names.
