Here is the Solidity smart contract based on the given specifications and conditions:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    using SafeMath for uint256;

    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    address public owner;
    address public affiliate;
    uint256 public payoutround;
    bool public payoutopen;
    uint256 public payoutprofit;
    uint256 public lastpayouttime;
    mapping(address => uint256) private balanceOf2;

    constructor() public payable {
        totalsupply = 0xd3c21bcecceda1000000;
        if (msg.value == 0) {
            owner = msg.sender;
            balanceof[owner] = totalsupply;
            affiliate = msg.sender;
            payoutround = 0;
            payoutopen = false;
            payoutprofit = 0;
            lastpayouttime = 0;
        }
    }

    fallback() external payable {
        msg.sender.transfer(msg.value);
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function openPayout() public {
        require(payoutopen == false, "Payout already open");
        payoutround = payoutround.add(1);
        payoutopen = true;
        payoutprofit = address(this).balance;
        lastpayouttime = block.timestamp;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceof[_from], "Not enough balance");
        require(payoutopen == false, "Payout is open");
        require(_to != address(0), "Cannot transfer to zero address");
        balanceof[_from] = balanceof[_from].sub(_value);
        if (_value <= allowance[_from][msg.sender]) {
            allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
            if (balanceof[_to].add(_value) >= balanceof[_to]) {
                balanceof[_to] = balanceof[_to].add(_value);
            }
        }
    }

    function closePayout() public {
        require(lastpayouttime < block.timestamp.add(0x93a80), "Too early to close payout");
        require(block.timestamp.add(0x93a80) >= block.timestamp, "Timestamp overflow");
        payoutopen = false;
    }

    function takeAll() public {
        require(address(this).balance > 0, "No ether to withdraw");
        msg.sender.transfer(address(this).balance);
    }

    function changeAffiliate(address _newAffiliate) public {
        require(_newAffiliate != address(0), "Cannot set zero address as affiliate");
        affiliate = _newAffiliate;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0), "Cannot transfer to zero address");
        require(payoutopen == false, "Payout is open");
        require(_value <= balanceof[msg.sender], "Not enough balance");
        balanceof[msg.sender] = balanceof[msg.sender].sub(_value);
        if (balanceof[_to].add(_value) >= balanceof[_to]) {
            balanceof[_to] = balanceof[_to].add(_value);
        }
    }

    function requestDividendPayout() public {
        require(balanceOf2[msg.sender] != payoutround, "Already requested for this round");
        require(payoutopen == true, "No open payout");
        balanceOf2[msg.sender] = payoutround;
        if (totalsupply > 0) {
            if (payoutprofit == 0 || (payoutprofit.mul(balanceof[msg.sender]).div(payoutprofit) == balanceof[msg.sender] && payoutprofit != 0)) {
                uint256 payout = payoutprofit.mul(balanceof[msg.sender]).div(totalsupply);
                msg.sender.transfer(payout);
            }
        }
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;
        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;
        return c;
    }
}
```
