```solidity
pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalsupply = 0;
    uint256 public rate = 100;
    uint256 public start = now;
    uint256 public period = 90;
    uint256 public hardcap = 0x152d02c7e14af6800000;
    uint256 public softcap = 0x6c6b935b8bbd400000;
    uint8 public percent = 30;
    uint256 public ethtotal = 0;
    mapping(address => uint256) public ethOf;

    constructor() public payable {
        owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function setPeriod(uint256 _period) public {
        require(msg.sender == owner && _period > 0);
        period = _period;
    }

    function setOwner(address _newOwner) public {
        require(_newOwner != address(0) && _newOwner != address(this) && msg.sender == owner);
        owner = _newOwner;
    }

    function finishICO(uint256 _value) public {
        require(ethtotal >= softcap && _value <= address(this).balance && now >= start && msg.sender == owner);
        msg.sender.transfer(_value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender] && _to != address(0) && _value <= balanceOf[_from]);
        balanceOf[_from] -= _value;
        if (_value + balanceOf[_to] >= balanceOf[_to]) {
            balanceOf[_to] += _value;
            allowance[_from][msg.sender] -= _value;
        }
    }

    function refund(uint256 _value) public {
        require(_value <= ethOf[msg.sender] && ethtotal < softcap && _value > 0 && now >= start + (0x15180 * period) && _value <= address(this).balance);
        ethOf[msg.sender] -= _value;
        msg.sender.transfer(_value);
        if (!msg.sender.send(_value) && _value <= ethtotal) {
            ethtotal -= _value;
        }
        if (balanceOf[msg.sender] > 0) {
            if(balanceOf[msg.sender] <= totalsupply) {
                totalsupply -= balanceOf[msg.sender];
            }
            balanceOf[msg.sender] = 0;
        }
    }

    function setHardcap(uint256 _hardcap) public {
        require(now >= start && msg.sender == owner && _hardcap > softcap);
        hardcap = _hardcap;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        if (_value + balanceOf[_to] >= balanceOf[_to]) {
            balanceOf[_to] += _value;
        }
    }

    fallback() external payable {
        require(now >= start && now <= start + (0x15180 * period) && ethtotal < hardcap && rate * msg.value > 0 && msg.sender != address(0) && rate * msg.value + totalsupply >= totalsupply && msg.value > 0);
        totalsupply += rate * msg.value;
        if (rate * msg.value + balanceOf[msg.sender] >= balanceOf[msg.sender]) {
            balanceOf[msg.sender] += rate * msg.value;
            if (msg.value + ethOf[msg.sender] >= ethOf[msg.sender]) {
                ethOf[msg.sender] += msg.value;
                if (msg.value + ethtotal >= ethtotal) {
                    ethtotal += msg.value;
                }
            }
        }
    }
}
```

Please note that this is a sample contract and should be thoroughly tested before being used in production. Also, Solidity contracts can be complex and there could be security vulnerabilities if not properly coded. Therefore, I would highly recommend having the contract audited by a professional security expert before deploying it on the main network.
