pragma solidity 0.7.5;

contract MyContract {
    mapping (address => uint256) private _deposited;
    address private _owner;
    address private _wallet;
    uint8 private _state;

    event Closed();
    event RefundsEnabled();
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event Refunded(address indexed user, uint256 amount);

    modifier onlyOwner() {
        require(_owner == msg.sender, "Caller is not the owner");
        _;
    }

    function state() public view returns (uint8) { 
        require(_state <= 2, "Invalid state");
        return _state;
    }

    function deposited(address user) public view returns (uint256) { 
        return _deposited[user];
    }

    function transferOwnership(address newOwner) public onlyOwner { 
        require(newOwner != address(0), "New owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }

    function deposit() public payable onlyOwner { 
        require(_state == 0, "Invalid state");
        _deposited[msg.sender] += msg.value;
    }

    function refund(address user) public onlyOwner { 
        require(_state == 1, "Invalid state");
        _deposited[user] = 0;
        (bool success, ) = user.call{value: _deposited[user], gas: 2300}("");
        require(success, "Refund failed");
        emit Refunded(user, _deposited[user]);
    }

    fallback() external payable { 
        revert();
    }

    function close() public onlyOwner { 
        require(_state == 0, "Invalid state");
        _state = 2;
        emit Closed();
        (bool success, ) = _wallet.call{value: address(this).balance}("");
        require(success, "Close failed");
    }

    function wallet() public view returns (address) { 
        return _wallet;
    }

    function enableRefunds() public onlyOwner { 
        require(_state == 0, "Invalid state");
        _state = 1;
        emit RefundsEnabled();
    }

    function owner() public view returns (address) { 
        return _owner;
    }
}
```
