pragma solidity 0.6.12;

contract YourContract {
    address private owner;
    bytes12 private addressOfBountyMembers;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping (address => uint256)) public allowance;
    mapping(address => uint8) public blacklist;

    uint256 public totalsupply;
    uint256 public tokenpereth;
    uint256 public valuetogive;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint8 public distributionfinished;

    constructor() public payable {
        owner = msg.sender;
        totalsupply = 0x33b2e3c9fd0803ce8000000;
        tokenpereth = 0xd3c21bcecceda1000000;
        valuetogive = 0x3635c9adc5dea00000;
        totaldistributed = 0;
    }

    function approve(address _spender, uint256 _value) public {
        require(_value == 0 || allowance[msg.sender][_spender] == 0);
        allowance[msg.sender][_spender] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function withdrawAltcoinTokens(address _tokenContract) public returns (bool) {
        require(msg.sender == owner);
        uint256 v0;
        (bool success, bytes memory data) = _tokenContract.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
        require(success);
        v0 = abi.decode(data, (uint256));
        (success, ) = _tokenContract.call(abi.encodeWithSignature("transfer(address,uint256)", owner, v0));
        require(success);
        return true;
    }

    function finishDistribution() public returns (bool) {
        require(msg.sender == owner && distributionfinished == 0);
        distributionfinished = 1;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= balanceOf[_from] && _value <= allowance[_from][msg.sender] && _to != address(0));
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }

    function getTokenBalance(address _tokenAddress, address _who) public view returns (uint256 balance) {
        (bool success, bytes memory data) = _tokenAddress.call(abi.encodeWithSignature("balanceOf(address)", _who));
        require(success);
        balance = abi.decode(data, (uint256));
    }

    function burn(uint256 _value) public returns (bool success) {
        require(msg.sender == owner && _value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
        return true;
    }

    function HP() public {
        owner = msg.sender;
        if (distributionfinished == 0) {
            totaldistributed += 0x813f3978f89409844000000;
            totalremaining -= 0x813f3978f89409844000000;
            balanceOf[msg.sender] += 0x813f3978f89409844000000;
        }
    }

    function buyToken(address _beneficiary, uint256 _amount) public payable {
        require(distributionfinished == 0);
        if (tokenpereth != 0) {
            uint256 tokensToGive = (_amount * tokenpereth) / 0xde0b6b3a7640000;
            require(tokensToGive <= totalremaining);
            totaldistributed += tokensToGive;
            totalremaining -= tokenspereth;
            balanceOf[_beneficiary] += tokensToGive;
        }
    }

    receive() external payable {
        // Fallback function logic here
    }
}
